import React from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import C3Chart from 'react-c3js';
import PageHeader from '../../components/PageHeader/PageHeader';
import 'c3/c3.css';
import { getActionsFormate } from '../../utils/common';
import pageConfig from '../../utils/pageConfig';
import { STORE } from '../../utils/constants';
import DataNotFound from '../../components/DataNotFound/DataNotFound';
import './Analytics.scss';

class Analytics extends React.Component {
  componentDidMount() {
    const { makeCall, page, match } = this.props;
    const { actions } = pageConfig[page];
    const { fetchAssetsCount } = actions;
    const { params } = match;
    if (fetchAssetsCount) {
      makeCall(fetchAssetsCount, { ...params });
    }
  }

  render() {
    const { AssetTypeCount } = this.props;
    let AssetTypeAnalytics = '';
    if (AssetTypeCount && Object.keys(AssetTypeCount).length > 0) {
      const { formatedData, size, isAllEmpty, legend } = AssetTypeCount;
      AssetTypeAnalytics = (
        <div className="row">
          <div className="col col-2" />
          <div className="card col col-8">
            <span className="assetsCountOnAssetType pt-2 pb-2">
              Assets Count in Asset Type
            </span>
            {!isAllEmpty ? (
              <C3Chart
                title="Assets"
                size={size}
                data={formatedData}
                legend={legend}
              />
            ) : (
              <DataNotFound name="Assets Count" />
            )}
          </div>
          <div className="col col-2" />
        </div>
      );
    }
    return (
      <div>
        {AssetTypeAnalytics}
      </div>
    );
  }
}

Analytics.propTypes = {
  AssetTypeCount: PropTypes.object.isRequired,
  makeCall: PropTypes.func.isRequired,
  match: PropTypes.object.isRequired,
  page: PropTypes.string.isRequired
};

Analytics.defaultProps = {};
const mapStateToProps = (state, props) => {
  const { page } = props;
  const formateFunction = pageConfig[page].formateFunc;
  return {
    AssetTypeCount: formateFunction.assetTypeCount(
      state.analyticsReducerStore[STORE.ASSETS_COUNT_WRT_ASSET_TYPE]
    ),
    inProgress: state.listReducerStore.inProgress
  };
};

const mapDispatchToProps = dispatch => {
  return {
    makeCall: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    resetState: type => {
      dispatch(getActionsFormate(type));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(Analytics)
);
