import React from 'react';
import PropTypes from 'prop-types';
import Loadable from 'react-loadable';
import { BrowserRouter, Route, Switch } from 'react-router-dom';

import { PAGE } from './utils/constants';
import AuthRoute from './components/AuthRoute';
import AppliedRoute from './components/AppliedRoute';
import RedirectRoute from './components/RedirectRoute';
// import LoaderComponent from './components/Loader/LoaderComponent';
import PageLoader from './components/PageLoader/PageLoader';
import {
  FOOTPRINTS_ROUTES,
  userSessionCookie,
  rememberUserCookie
} from './config';
import GenerateQrCodes from './containers/GenerateQrCodes/GenerateQrCodes';

const delayTime = 60;
// Login
const AsyncLogin = Loadable({
  loader: () => import('./containers/Login/Login'),
  loading: PageLoader,
  delay: delayTime
});

const AsyncQrCodes = Loadable({
  loader: () => import('./containers/QRCodes/QRCodes'),
  loading: PageLoader,
  delay: delayTime
});

// Invalid Resource
const AsyncNotFound = Loadable({
  loader: () => import('./containers/NotFound'),
  loading: PageLoader,
  delay: delayTime
});

const Routes = ({ childProps }) => {
  const props = {
    ...childProps,
    sessionCookie: userSessionCookie,
    rememberCookie: rememberUserCookie,
    redirectionRoute: FOOTPRINTS_ROUTES.ANALYTICS
  };
  return (
    <BrowserRouter basename="">
      <>
        <Switch>
          <RedirectRoute path="/" exact component={AsyncLogin} props={props} />
          <AppliedRoute
            path={FOOTPRINTS_ROUTES.LOGIN}
            exact
            component={AsyncLogin}
            props={props}
          />

          <AppliedRoute
            path={FOOTPRINTS_ROUTES.FORGOT_PASSWORD}
            exact
            component={AsyncForgotPassword}
            props={props}
          />

          <AppliedRoute
            path={FOOTPRINTS_ROUTES.PASSWORD_RESET}
            exact
            component={AsyncPasswordReset}
            props={props}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.ANALYTICS}
            exact
            component={AsyncAnalytics}
            props={{ ...props, page: PAGE.ANALYTICS }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.ANALYTICS}
            exact
            component={AsyncAnalytics}
            props={{ ...props, page: PAGE.ANALYTICS }}
          />

          {/* Organization Units */}
          <AuthRoute
            path={FOOTPRINTS_ROUTES.ORG_UNITS.LIST}
            exact
            component={AsyncListPage}
            props={{ ...props, page: PAGE.ORG_UNITS }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.ORG_UNITS.ADD}
            exact
            component={AsyncAddEditOrgUnit}
            props={{ ...props, page: PAGE.ADD_ORG_UNIT }}
          />

          {/* Organization Units Details */}
          <AuthRoute
            path={FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.SHOW}
            exact
            // component={AsyncListPage}
            component={AsyncDetails}
            props={{ ...props, page: PAGE.ORG_UNITS_DETAILS }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.MODIFY}
            exact
            component={AsyncAddEditOrgUnit}
            props={{ ...props, page: PAGE.UPDATE_ORG_UNIT }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.ASSET.LIST}
            exact
            component={AsyncAssets}
            props={{ ...props, page: PAGE.ASSET }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.ASSET.DETAIL.SHOW}
            exact
            component={AsyncDetails}
            props={{ ...props, page: PAGE.ASSET_DETAILS }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.QR_CODES.LIST}
            exact
            component={AsyncQrCodes}
            props={{ ...props, page: PAGE.QR_CODES }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.QR_CODES.ADD}
            exact
            component={AsyncGenerateQrCodes}
            props={{ ...props, page: PAGE.ADD_QR_CODES }}
          />

          {/* Users */}
          <AuthRoute
            path={FOOTPRINTS_ROUTES.USERS.LIST}
            exact
            component={AsyncListPage}
            props={{
              ...props,
              page: PAGE.USERS
            }}
          />

          {/* Asset Types */}
          <AuthRoute
            path={FOOTPRINTS_ROUTES.ASSET_TYPES.LIST}
            exact
            component={AsyncListPage}
            props={{ ...props, page: PAGE.ASSET_TYPES }}
          />

          {/* Asset Attributes */}
          <AuthRoute
            path={FOOTPRINTS_ROUTES.ASSET_ATTRIBUTES.LIST}
            exact
            component={AsyncListPage}
            props={{ ...props, page: PAGE.ASSET_ATTRIBUTES }}
          />

          {/* Add Edit Asset Attributes */}
          <AuthRoute
            path={FOOTPRINTS_ROUTES.ASSET_ATTRIBUTES.ADD}
            exact
            component={AsyncAddEditAssetAttributes}
            props={{ ...props, page: PAGE.ADD_ASSET_ATTRIBUTES }}
          />

          {/* Add Asset Types */}
          <AuthRoute
            path={FOOTPRINTS_ROUTES.ASSET_TYPES.ADD}
            exact
            component={AsyncAddEditAssetTypes}
            props={{ ...props, page: PAGE.ADD_ASSET_TYPES }}
          />

          {/* Asset Types Details */}
          <AuthRoute
            path={FOOTPRINTS_ROUTES.ASSET_TYPES.DETAIL.SHOW}
            exact
            component={AsyncDetails}
            props={{ ...props, page: PAGE.ASSET_TYPES_DETAILS }}
          />

          {/* Asset Attributes Details */}
          <AuthRoute
            path={FOOTPRINTS_ROUTES.ASSET_ATTRIBUTES.DETAIL.SHOW}
            exact
            component={AsyncDetails}
            props={{ ...props, page: PAGE.ASSET_ATTRIBUTE_DETAILS }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.USERS.ADD}
            exact
            component={AsyncAddEditUser}
            props={{ ...props, page: PAGE.ADD_USER }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.USERS.DETAIL.SHOW}
            exact
            component={AsyncDetails}
            props={{ ...props, page: PAGE.USER_DETAILS }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.USERS.DETAIL.MODIFY}
            exact
            component={AsyncAddEditUser}
            props={{ ...props, page: PAGE.UPDATE_USER }}
          />

          {/* Asset Attributes Update */}
          <AuthRoute
            path={FOOTPRINTS_ROUTES.ASSET_ATTRIBUTES.DETAIL.MODIFY}
            exact
            component={AsyncAddEditAssetAttributes}
            props={{ ...props, page: PAGE.UPDATE_ASSET_ATTRIBUTES }}
          />

          {/* Asset Types Update */}
          <AuthRoute
            path={FOOTPRINTS_ROUTES.ASSET_TYPES.DETAIL.MODIFY}
            exact
            component={AsyncAddEditAssetTypes}
            props={{ ...props, page: PAGE.UPDATE_ASSET_TYPES }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.ASSET.LIST}
            exact
            component={AsyncAssets}
            props={{ ...props, page: PAGE.ASSET }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.ASSET.DETAIL.SHOW}
            exact
            component={AsyncDetails}
            props={{ ...props, page: PAGE.ASSET_DETAILS }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.QR_CODES.LIST}
            exact
            component={AsyncQrCodes}
            props={{ ...props, page: PAGE.QR_CODES }}
          />

          <AuthRoute
            path={FOOTPRINTS_ROUTES.QR_CODES.ADD}
            exact
            component={AsyncGenerateQrCodes}
            props={{ ...props, page: PAGE.ADD_QR_CODES }}
          />

          {/* Finally, catch all unmatched routes */}
          <Route component={AsyncNotFound} />
        </Switch>
      </>
    </BrowserRouter>
  );
};

Routes.propTypes = {
  childProps: PropTypes.element
};

Routes.defaultProps = {
  childProps: <></>
};

export default Routes;
