import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import './UserDetails.scss';
import { getActionsFormate } from '../../utils/common';
import ACTIONS from '../../apiConfig/actions.constants';
import UserInfo from '../../components/UserInfo/UserInfo';
import pageConfig from '../../utils/pageConfig';
import PageHeader from '../../components/PageHeader/PageHeader';

class UserDetails extends React.Component {
  componentDidMount() {
    const { getUserDetials, match } = this.props;
    const { params } = match;
    const type = ACTIONS.USERS.GET_USER_INFO;
    getUserDetials(type, { ...params });
  }

  componentDidMount() {
    if (prevProps.match.url !== this.props.match.url) {
    const { getUserDetials, match } = this.props;
    const { params } = match;
    const type = ACTIONS.USERS.GET_USER_INFO;
    getUserDetials(type, { ...params });
    }
  }

  render() {
    const { page, userData, redirect } = this.props;
    const { pageTitle, headerActions } = pageConfig[page];
    const fullName =
      userData && (userData.first_name || userData.last_name)
        ? `${userData.first_name} ${userData.last_name}`
        : '';
    return (
      <div className="user-details">
        <PageHeader title={fullName || pageTitle} actions={headerActions} redirect={redirect}/>
        <UserInfo {...this.props} userData={userData}/>
      </div>
    );
  }
}

UserDetails.propTypes = {
  userData: PropTypes.oneOfType([PropTypes.array, PropTypes.object]).isRequired,
  getUserDetials: PropTypes.func.isRequired,
  page: PropTypes.string.isRequired,
  match: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  userData: state.userDetailsReducerStore.data
});

const mapDispatchToProps = dispatch => {
  return {
    getUserDetials: (type, data) => {
      dispatch(getActionsFormate(type, data));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(UserDetails)
);
