import React from 'react';
import Tooltip from '@atlaskit/tooltip';
import Select, { components } from 'react-select';
import PropTypes from 'prop-types';
import './CustomSelect.scss';
const Input = props => {
  if (props.isHidden) {
    return <components.Input {...props} />;
  }
  return (
    <div>
      <Tooltip content="Custom Input" className="pl-0">
        <components.Input className="pl-0" {...props} />
      </Tooltip>
    </div>
  );
};

const CustomSelect = props => {
  return (
    <div className="row">
      <div className="custom-select-roles col col-10">
        <Select
          value={props.values}
          closeMenuOnSelect={false}
          components={{ Input }}
          onChange={props.onChange}
          options={props.fieldValues}
          isMulti
          placeholder={props.placeholder}
          id="custom-select"
        />
      </div>
      <div aria-hidden="true" class=" css-tlfecz-indicatorContainer">
         <svg height="20" width="20" viewBox="0 0 20 20" aria-hidden="true" focusable="true" class="css-tj5bde-Svg"><path d="M4.516 7.548c0.436-0.446 1.043-0.481 1.576 0l3.908 3.747 3.908-3.747c0.533-0.481 1.141-0.446 1.574 0 0.436 0.445 0.408 1.197 0 1.615-0.406 0.418-4.695 4.502-4.695 4.502-0.217 0.223-0.502 0.335-0.787 0.335s-0.57-0.112-0.789-0.335c0 0-4.287-4.084-4.695-4.502s-0.436-1.17 0-1.615z"></path></svg>
        </div>
    </div>
  );
};
CustomSelect.propTypes = {
  onChange: PropTypes.func.isRequired,
  fieldValues: PropTypes.string.isRequired,
  placeholder: PropTypes.string.isRequired,
  values: PropTypes.array.isRequired
};
Input.propTypes = {
  isHidden: PropTypes.bool.isRequired
};

export default CustomSelect;
