import React from 'react';
import Tooltip from '@atlaskit/tooltip';
import Select, { components } from 'react-select';
import PropTypes from 'prop-types';
import './CustomSelect.scss';

const Input = props => {
  if (props.isHidden) {
    return <components.Input {...props} />;
  }
  return (
    <div>
      <Tooltip content="Custom Input" className="pl-0">
        <components.Input className="pl-0" {...props} />
      </Tooltip>
    </div>
  );
};

const CustomSelect = props => {
  return (
    <div className="custom-select-roles">
      <div className=" row">
        <Select
          value={props.values}
          closeMenuOnSelect={false}
          components={{ Input }}
          onChange={props.onChange}
          options={props.fieldValues}
          isMulti
          placeholder={props.placeholder}
          className="col col-10 custom-roles-select-drop-down w-100"
        />
      </div>
    </div>
  );
};
CustomSelect.propTypes = {
  onChange: PropTypes.func,
  fieldValues: PropTypes.oneOfType([PropTypes.array, PropTypes.string]),
  placeholder: PropTypes.string,
  values: PropTypes.array
};
Input.propTypes = {
  isHidden: PropTypes.bool
};

export default CustomSelect;
