import { call, put } from 'redux-saga/effects';

import ACTIONS from '../../apiConfig/actions.constants';
import { FOOTPRINTS_API } from '../../apiConfig/api.config';

function* getUserList(action) {
  let response;
  try {
    response = yield call(window.axios.getData, FOOTPRINTS_API.USER);

    if (response.status === 200) {
      yield put({
        type: ACTIONS.USERS.GET_USER_LIST_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.USERS.GET_USER_LIST_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({ type: ACTIONS.USERS.GET_USER_LIST_FAIL, payload: e });
  }
}

function* getUserInfo(action) {
  let response;
  try {
    console.log("Actions:", action);
    const { data } = this.props;
    const API_URL = FOOTPRINTS_API.USER + data.userId;
    response = yield call(window.axios.getData, API_URL);
    console.log("Response:", response);
    if (response.status === 200) {
      data.props.history.push(data.url);
      yield put({
        type: ACTIONS.USERS.GET_USER_INFO_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.USERS.GET_USER_INFO_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({ type: ACTIONS.USERS.GET_USER_INFO_FAIL, payload: e });
  }
}

function* createUser(action) {
  let response;
  try {
    response = yield call(
      window.axios.postData,
      FOOTPRINTS_API.USER,
      action.data
    );
    if (response.status === 200) {
      yield put({
        type: ACTIONS.USERS.ADD_USER_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.USERS.ADD_USER_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({ type: ACTIONS.USERS.ADD_USER_FAIL, payload: e });
  }
}

function* updateUser(action) {
  let response;
  try {
    response = yield call(
      window.axios.putData,
      FOOTPRINTS_API.USER,
      action.data
    );
    if (response.status === 200) {
      yield put({
        type: ACTIONS.USERS.UPDATE_USER_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.USERS.UPDATE_USER_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({ type: ACTIONS.USERS.UPDATE_USER_FAIL, payload: e });
  }
}

const dummy = () => {};

export { getUserList, getUserInfo, createUser, updateUser, dummy };
