import React from 'react';
import PropTypes from 'prop-types';
import './AssetDetails.scss';
import { getFormatedDate, isValidData } from '../../utils/common';
import { ASSET_STATUS } from '../../utils/constants';
import CustomLogs from '../CustomLogs/CustomLogs';

const AssetDetails = props => {
  const { Data, historyData } = props;
  if (isValidData(Data)) {
    const {
      asset_type: AssetType,
      attributes: Attributes,
      due_date: DueDate,
      state: Status,
      submitted_date: SubmittedDate
    } = Data;
    let customKey = 0;
    const attributeElements =
      Attributes &&
      Attributes instanceof Array &&
      Attributes.length > 0 &&
      Attributes.map(each => {
        customKey += 1;
        if (isValidData(each) && each.attribute && each.attribute_value) {
          const {
            attribute: Attribute,
            attribute_value: AttributeValue
          } = each;
          return (
            <div className="asset-details-body row" key={customKey}>
              <span className="asset-details-key col">{Attribute.name}</span>
              <span className="asset-details-key">:</span>
              <span className="asset-details-value col">
                {AttributeValue.name}
              </span>
            </div>
          );
        }
        return true;
      });


    return (
      <div className="asset-details-page text-capitalize">
        <div className="row">
          <div className="col col-11 col-md-5 ml-2 p-2 details-title">
            {' '}
            Details{' '}
          </div>
          <div className="col col-11 col-md-5 ml-2 p-2 details-title d-none d-md-block">
            {' '}
            History{' '}
          </div>
        </div>
        <div className="row ">
          <div className="col col-11 col-md-5 ml-2 p-2 asset-detials">
            <div className="asset-details-body row">
              <span className="asset-details-key col">asset type</span>
              <span className="asset-details-key">:</span>
              <span className="asset-details-value col">
                {AssetType ? AssetType.name : false}
              </span>
            </div>
            <div className="asset-details-body row">
              <span className="asset-details-key col">Submitted Date</span>
              <span className="asset-details-key">:</span>
              <span className="asset-details-value col">
                {getFormatedDate(SubmittedDate)}
              </span>
            </div>
            <div className="asset-details-body row">
              <span className="asset-details-key col">Due Date</span>
              <span className="asset-details-key">:</span>
              <span className="asset-details-value col">
                {getFormatedDate(DueDate)}
              </span>
            </div>
            <div className="asset-details-body row">
              <span className="asset-details-key col">Status</span>
              <span className="asset-details-key">:</span>
              <span className="asset-details-value col">
                {ASSET_STATUS[Status]}
              </span>
            </div>

            <div id="asset-attributs-title" className="mb-3 mt-3">
              <span>Attributes</span>
            </div>
            {attributeElements && attributeElements.length > 0 ? (
              attributeElements
            ) : (
              <span>No attributes found</span>
            )}
          </div>
          <div className="col d-md-none mb-3 mt-3 details-title"> History </div>
          <div className="card col col-11 col-md-5 ml-3 p-2">
            <CustomLogs historyData={historyData}/>
          </div>
        </div>
      </div>
    );
  }
  return null;
};

AssetDetails.propTypes = {
  Data: PropTypes.oneOfType([PropTypes.array, PropTypes.object]),
  historyData: PropTypes.oneOfType([PropTypes.array, PropTypes.object])
};

AssetDetails.defaultProps = {
  Data: {},
  historyData: []
};
export default AssetDetails;
