import React from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import 'react-dropdown-tree-select/dist/styles.css';
import './CustomTreeSelect.scss';
import DropdownTreeSelect from 'react-dropdown-tree-select';
import pageConfig from '../../utils/pageConfig';

class CustomTreeSelect extends React.Component {

  componentDidMount() {
    const { getOrgUnit, page } = this.props;
    const dtype = pageConfig[page].actions.getAllOrgUnit;
    console.log("asdasd", dtype);

    getOrgUnit(dtype);
  }

  render(){
    return (
      <DropdownTreeSelect
        texts={{ placeholder: 'Departments' }}
        data={props.data}
        onChange={props.onChange}
        mode={'radioSelect'}
      />
    );
  }

};

const mapStateToProps = (state, props) => {
  const {page} = props;
  const getFormate = pageConfig[page].formates.orgUnits;
  return ({
    errorCode: state.addEditOrgUnitReducerStore.errorCode,
    allOrgUnit: getFormate(state.listReducerStore[STORE.ALL_ORG_UNIT])
  })
}

const mapDispatchToProps = dispatch => {
  return {
    getOrgUnit: (type) =>{
      dispatch(getActionsFormate(type));
    }
  };
};

export default  withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(CustomTreeSelect)
);
