  return {
    getParentDepartments: (dtype, data) => {
      dispatch(getActionsFormate(dtype, data));
    },
    submit: (type, data) => {
      dispatch(getActionsFormate(type, data));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(AddEditOrgUnits)
);
