import React from 'react';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCircle } from '@fortawesome/free-regular-svg-icons';
import { faCheckCircle } from '@fortawesome/free-solid-svg-icons';

import './Checkbox.scss';

const Checkbox = props => {
  const checkboxIcon = props.checked
    ? { icon: faCheckCircle, class: 'checked' }
    : { icon: faCircle, class: '' };
  return (
    <div className="checkbox-wrapper text-left clickable">
      <div onClick={props.onCheck} role="button" id={props.id}>
        <span className={`checkbox-icon ${checkboxIcon.class}`}>
          <FontAwesomeIcon icon={checkboxIcon.icon} size="lg" />
        </span>
        <label className="clickable">
          <span>{props.labelText}</span>
        </label>
      </div>
    </div>
  );
};

Checkbox.propTypes = {
  id: PropTypes.string,
  checked: PropTypes.bool,
  onCheck: PropTypes.func.isRequired,
  labelText: PropTypes.string.isRequired
};

Checkbox.defaultProps = {
  checked: false,
  id: 'checkboxButton'
};

export default Checkbox;
