import React from 'react';
import PropTypes from 'prop-types';
import { getFormatedDate } from '../../utils/common';
import { LOG_TYPE } from '../../utils/constants';
import DataNotFound from '../DataNotFound/DataNotFound';

const CustomLogs =props=> {
    const { historyData} = props;
    let historyKey = 0;
    const showHistory =
      historyData &&
      historyData instanceof Array &&
      historyData.map(eachLog => {
        historyKey += 1;
        const {
          timestamp,
          attributes,
          log_type: logType,
          agent,
          org_unit: OrgUnit,
          assignee,
          notes
        } = eachLog;
        let agentName = '';
        let assigneeName = '';
        if (agent) {
          const { first_name: firstName, last_name: lastName } = agent;
          agentName = `${firstName} ${lastName}`;
        }

        if (assignee) {
          const { first_name: firstName, last_name: lastName } = assignee;
          assigneeName = `${firstName} ${lastName}`;
        }

        let logsAttributs = [];
        let attributeKey = 0;
        if (attributes) {
          logsAttributs =
            attributes instanceof Array &&
            attributes.map(eachAttr => {
              const {
                attribute: Attribute,
                attribute_value: AttributeValue
              } = eachAttr;
              attributeKey += 1;
              return (
                <div className="history-log-body-value row" key={attributeKey}>
                  <span className="font-weight-bold col">{Attribute.name}</span>
                  <span className="font-weight-bold">:</span>
                  <span className="col">{AttributeValue.name}</span>
                </div>
              );
            });
        }

        return (
          <div className="history-item row" key={historyKey}>
            <div className="col col-4 ml-xl-n2 ml-md-n1 ml-n2 date-time">
              {getFormatedDate(timestamp)}
            </div>
            <div
              className={`${
                historyKey === historyData.length
                  ? 'item-data last'
                  : 'item-data'
              } w-100 col col-7`}
            >
              <div className="border p-1 item-data-type">
                <strong>{LOG_TYPE[logType]}</strong>
                {agentName && (
                  <div className="asset-changed-by mb-1">
                    Changed by{' '}
                    <span className="font-weight-bold">{agentName}</span>
                  </div>
                )}
                {logsAttributs.length > 0 && { logsAttributs }}
                {OrgUnit && (
                  <div className="history-log-body-key">
                    Org unit:{' '}
                    <div className="history-log-body-value ml-2">
                      {' '}
                      {OrgUnit.name}{' '}
                    </div>
                  </div>
                )}
                {assigneeName && (
                  <div className="history-log-body-key">
                    assignee:{' '}
                    <div className="history-log-body-value ml-2">
                      {' '}
                      {assigneeName}{' '}
                    </div>
                  </div>
                )}
                {notes && (
                  <div className="history-log-body-key">
                    notes:{' '}
                    <div className="history-log-body-value ml-2">{notes}</div>
                  </div>
                )}
              </div>
            </div>
          </div>
        );
      });
      return (
            <div className="events w-100">
                {showHistory && showHistory.length > 0 ? (
                showHistory
                ) : (
                <DataNotFound name="History" />
                )}
        </div>
      )
}
CustomLogs.propTypes = {
    historyData: PropTypes.oneOfType([PropTypes.array, PropTypes.object])
};

CustomLogs.defaultProps = {
    Data: {},
    historyData: []
};
export default CustomLogs;
