import React from 'react';
import PropTypes from 'prop-types';
import './CustomList.scss';

class CustomList extends React.Component {
    constructor(props){
        super(props);
        this.state={
            showDescription: false
        }
    }

    handleDescription = ()=>{
        this.setState({showDescription:!this.state.showDescription})
    }

    render(){
        const {data} = this.props;
        const listEle = data && data.map(each=>{
        return <div className="col col-12 border m-2 " onClick={eve=>this.handleDescription()}>
            <div className="p-1 todo-title">{each.title}</div>
            {this.state.showDescription && <div className="p-1 todo-description">{each.description}</div>}  
        </div>
        })
        return (<div className="row todo-list">
            {listEle}
        </div>)
    }
}

CustomList.propTypes = {
    data: PropTypes.array,
    paginationData: PropTypes.object.isRequired
};

CustomList.defaultProps = {
    data: []
};
  export default CustomList;
