import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import './Details.scss';
import { getActionsFormate } from '../../utils/common';
import pageConfig from '../../utils/pageConfig';
import PageHeader from '../../components/PageHeader/PageHeader';
import { PAGE } from '../../utils/constants';

class Details extends React.Component {
  componentDidMount() {
    const { getDetials, match, page } = this.props;
    const { actions } = pageConfig[page];
    const { params } = match;
    const type = actions && actions.fetchDetails;
    getDetials(type, { ...params });

  }


  componentDidUpdate(prevProps) {
    if (prevProps.match.url !== this.props.match.url) {

    }
  }

  render() {
    const { page, Data } = this.props;
    const { pageTitle, headerActions, component: Component, redirect } = pageConfig[page];
    const fullName =
      page === PAGE.USER_DETAILS && Data && (Data.first_name || Data.last_name)
        ? `${Data.first_name} ${Data.last_name}`
        : '';
    const assetAttributeTitle =
      page === PAGE.ASSET_ATTRIBUTES_DETAILS && Data ? Data.name : '';
    const assetTypeTitle =
      page === PAGE.ASSET_TYPES_DETAILS && Data ? Data.doc_type : '';

    return (
      <div className="details">
        <PageHeader
         {...this.props}
          title={fullName || assetAttributeTitle || assetTypeTitle || pageTitle}
          actions={headerActions}
          redirect={redirect}
        />
        <Component {...this.props} Data={Data} />
      </div>
    );
  }
}

Details.propTypes = {
  Data: PropTypes.oneOfType([PropTypes.array, PropTypes.object]).isRequired,
  getDetials: PropTypes.func.isRequired,
  page: PropTypes.string.isRequired,
  match: PropTypes.object.isRequired,
  redirect: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
  Data: state.DetailsReducerStore.data
});

const mapDispatchToProps = dispatch => {
  return {
    getDetials: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    resetProps: (type) => {
      dispatch(getActionsFormate(type))
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(Details)
);
