import { reverse } from 'named-urls';
import { PAGE, STORE,TODOS, ADD, ADD_TODO, UPDATE_TODO } from './constants';
import { TODOS_ROUTE } from '../config';

import {
  redirect,
} from './utils';

export default {
  [PAGE.TODOS]: {
    pageTitle: TODOS,
    headerActions: [
        {
          text: ADD,
          handler: redirect,
        }
      ],
    redirect: {
      [ADD]: reverse(TODOS_ROUTE.TODOS.ADD)
    },
  },

  [PAGE.ADD_TODO]: {
    pageTitle: ADD_TODO,
  },

  [PAGE.UPDATE_TODO]: {
    pageTitle: UPDATE_TODO
  },

};
