import { call, put } from 'redux-saga/effects';

import ACTIONS from '../../apiConfig/actions.constants';
import { FOOTPRINTS_API } from '../../apiConfig/api.config';

function* loginSaga(action) {
  let response;
  try {
    response = yield call(
      window.axios.postData,
      FOOTPRINTS_API.LOGIN,
      action.data
    );
    console.log("Login saga:", response);
    if (response.status === 200) {
      yield put({
        type: ACTIONS.AUTH.LOGIN_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.AUTH.LOGIN_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({ type: ACTIONS.AUTH.LOGIN_FAIL, payload: e });
  }
}

const dummy = () => {};

export { loginSaga, dummy };
