import React, { useEffect } from 'react';
import PropTypes from 'prop-types';
import { Breadcrumb } from 'react-bootstrap';
import { withRouter } from 'react-router-dom';
import { connect } from 'react-redux';
import { reverse } from 'named-urls';
import { getActionsFormate } from '../../utils/common';
import ACTIONS from '../../apiConfig/actions.constants';
import { FOOTPRINTS_ROUTES } from '../../config';
import './BreadCrumb.scss';

function BreadCrumb(props) {
  useEffect(() => {
    const { makeCall, orgId, orgUnitId } = props;
    const id = orgId || Number(orgUnitId);
    if (id) {
      makeCall(ACTIONS.BREADCRUMB.GET_BREADCRUMB, id);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [props.orgId || props.orgUnitId]);

  function breadcrumbItems(path) {
    const pathList = [];
    if (path.length > 0) {
      for (let i = path.length - 1; i >= 0; i -= 1) {
        pathList.push(
          <Breadcrumb.Item
            href={breadcrumbPath(path[i].id)}
            key={path[i].id}
            active={i === 0}
          >
            {path[i].name}
          </Breadcrumb.Item>
        );
      }
    }
    return pathList;
  }

  function breadcrumbPath(orgUnitId) {
    const url = reverse(FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.SHOW, { orgUnitId });
    return url;
  }

  return (
    <div className="bread-crumb">
      <Breadcrumb>
        <Breadcrumb.Item href={FOOTPRINTS_ROUTES.ORG_UNITS.LIST}>
          Department
        </Breadcrumb.Item>
        {props.data ? breadcrumbItems(props.data) : null}
      </Breadcrumb>
    </div>
  );
}

BreadCrumb.propTypes = {
  // eslint-disable-next-line react/require-default-props
  orgId: PropTypes.number,
  // eslint-disable-next-line react/require-default-props
  data: PropTypes.array,
  // eslint-disable-next-line react/require-default-props
  orgUnitId: PropTypes.string
};

const mapStateToProps = (state, props) => {
  return {
    data: state.breadcrumbReducerStore.BREADCRUMB,
    orgId: state.listReducerStore.orgId
  };
};

const mapDispatchToProps = dispatch => {
  return {
    makeCall: (type, id) => {
      dispatch(getActionsFormate(type, id));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(BreadCrumb)
);
