import React from 'react';
import PropTypes from 'prop-types';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import ERROR_CODES from '../../utils/errorCodes';
import ACTIONS from '../../apiConfig/actions.constants';
import { ALERT_MESSAAGEs } from '../../utils/constants';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
class AlertMessage extends React.Component {
    notify = (status, errorCode) =>{
        console.log("status", status);

        if(status === ACTIONS.ALERT_MESSAGE.ADD_SUCCESS){
            toast.success(ALERT_MESSAAGEs[ACTIONS.ALERT_MESSAGE.ADD_SUCCESS], {
                position: toast.POSITION.TOP_RIGHT
            });
        }
        if(status === ACTIONS.ALERT_MESSAGE.UPDATE_SUCCESS){
            toast.success(ALERT_MESSAAGEs[ACTIONS.ALERT_MESSAGE.ADD_SUCCESS], {
                position: toast.POSITION.TOP_RIGHT
            });
        }
        if(status === ACTIONS.ALERT_MESSAGE.FAIL){
            console.log("comming here:", status);
            toast.error(ERROR_CODES["LIST"][errorCode], {
                position: toast.POSITION.TOP_RIGHT,
            });
        }

    };
    render() {
        const { AlertMessageStore } = this.props;
        const { status, errorCode } = AlertMessageStore;
        return (
            <div>
                {this.notify(status, errorCode)}
                <ToastContainer />
            </div>
        )
    }
}


AlertMessage.propTypes = {
    status: PropTypes.string.isRequired,
    errorCode: PropTypes.number.isRequired
};

const mapStateToProps = state => ({
    AlertMessageStore: state.AlertMessageReducerStore
});

const mapDispatchToProps = dispatch => {
    return {
      resetState: type => {
        dispatch(getActionsFormate(type));
      }
    };
  };
export default withRouter(
    connect(
      mapStateToProps,
      null
    )(AlertMessage)
  );
