import ACTIONS from '../../apiConfig/actions.constants';
import { getFormatOfdData } from '../../utils/utils';
import { STORE } from '../../utils/constants';

const initialState = {
  [STORE.GET_USER_DEPARTMENTS]: [],
  errorCode: '',
  inProgress: false
};

const addEditUserReducerStore = (stateParam, { type, payload }) => {
  const state = stateParam || initialState;
  switch (type) {
    case ACTIONS.USERS.ADD_USER:
      return { ...state, data: payload, inProgress: true };
    case ACTIONS.USERS.ADD_USER_SUCCESS:
      return { ...state, data: payload, inProgress: false };
    case ACTIONS.USERS.ADD_USER_FAIL:
      return { ...state, errorCode: payload.error_code, inProgress: false };

    case ACTIONS.USERS.UPDATE_USER:
      return { ...state, data: payload, inProgress: true };
    case ACTIONS.USERS.UPDATE_USER_SUCCESS:
      return { ...state, data: payload, inProgress: false };
    case ACTIONS.USERS.UPDATE_USER_FAIL:
      return { ...state, data: payload, inProgress: false };

    case ACTIONS.USERS.GET_USER_DEPARTMENTS:
      return { ...state, departments: payload, inProgress: true };
    case ACTIONS.USERS.GET_USER_DEPARTMENTS_SUCCESS: {
      const formatedData = getFormatOfdData(payload);
      return {
        ...state,
        [STORE.GET_USER_DEPARTMENTS]: formatedData,
        inProgress: false
      };
    }
    case ACTIONS.USERS.GET_USER_DEPARTMENTS_FAIL:
      return { ...state, data: payload, inProgress: false };

    case ACTIONS.USERS.RESET_STATE:
      return { ...state, errorCode: '', inProgress: false };
    default:
      return state;
  }
};

export default addEditUserReducerStore;
