import { faObjectGroup, faBuilding } from '@fortawesome/free-regular-svg-icons';
import {
  faUsers,
  faShapes,
  faChartLine
} from '@fortawesome/free-solid-svg-icons';

import { FOOTPRINTS_ROUTES } from '../config';

const LOGIN_FORM_HEADER = 'Sign In';
const LOGIN_BUTTON_TEXT = 'Sign In';

const PASSWORD_RESET_HEADER = 'Password Reset';
const PASSWORD_RESET_TOKEN_HEADER = 'password-reset-token';

const CHANGE_PASSWORD_HEADER = 'Change Password';

const PAGE = {
  LOGIN: 'LOGIN',
  FORGOT_PASSWORD: 'FORGOT_PASSWORD',
  CHANGE_PASSWORD: 'CHANGE_PASSWORD',
  RESET_PASSWORD: 'RESET_PASSWORD',
  ANALYTICS: 'ANALYTICS',
  ORG_UNITS: 'ORG_UNITS',
  ASSETS_TYPE: 'ASSETS_TYPE',
  USERS: 'USERS'
};

// API call status
const CALL_STATUS = {
  ERROR: 'error',
  PENDING: 'pending',
  RESPONDED: 'responded',
  IN_PROGRESS: 'in-progress',
  RESEND_ERROR: 'resend-error',
  TOKEN_ERROR: 'TOKEN_ERROR',
  TOKEN_RESPONDED: 'TOKEN_RESPONDED'
};

const SIDENAV_ITEMS = [
  {
    position: 1,
    displayName: 'Analytics',
    route: FOOTPRINTS_ROUTES.ANALYTICS,
    icon: faChartLine
  },
  {
    position: 2,
    displayName: 'Org Units',
    route: FOOTPRINTS_ROUTES.ORG_UNITS,
    icon: faBuilding
  },
  {
    position: 3,
    displayName: 'Users',
    route: FOOTPRINTS_ROUTES.USERS,
    icon: faUsers
  },
  {
    position: 4,
    displayName: 'Asset Types',
    route: FOOTPRINTS_ROUTES.ASSETS_TYPE,
    icon: faObjectGroup
  },
  {
    position: 5,
    displayName: 'Asset Attributes',
    route: FOOTPRINTS_ROUTES.ASSETS_TYPE,
    icon: faShapes
  }
];

export {
  PAGE,
  CALL_STATUS,
  SIDENAV_ITEMS,
  LOGIN_FORM_HEADER,
  LOGIN_BUTTON_TEXT,
  PASSWORD_RESET_HEADER,
  CHANGE_PASSWORD_HEADER,
  PASSWORD_RESET_TOKEN_HEADER
};
