import React from 'react';
import PropTypes from 'prop-types';
import cx from 'classnames';
import './ModalWrapper.scss';

const ESC_KEY = 27;
const noScrollClass = 'no-scroll';
class ModalWrapper extends React.Component {
  componentDidMount() {
    document.body.classList.add(noScrollClass);
    document.addEventListener('keydown', this.onEsc.bind(this), false);
  }

  componentWillUnmount() {
    document.body.classList.remove(noScrollClass);
    document.removeEventListener('keydown', this.onEsc.bind(this), false);
  }

  onEsc(e) {
    if (e.keyCode === ESC_KEY) {
      this.closeModal();
    }
  }

  closeModal = () => {
    this.props.onClose();
  };

  render() {
    const { show, title, children, lgModal } = this.props;
    const modalClassNames = cx('modal-dialog', 'modal-dialog-centered', {
      'modal-lg': lgModal
    });
    return (
      <div className="modal-wrapper">
        <div className={show ? 'modal-backdrop' : 'modal-backdrop d-none'}>
          <div
            className={show ? 'modal fade show' : 'modal fade'}
            tabIndex="-1"
            role="dialog"
          >
            <div className={modalClassNames} role="document">
              <div className="modal-content">
                <div className="modal-header">
                  <h5 className="modal-title">{title}</h5>
                  <button
                    type="button"
                    className="close"
                    onClick={this.closeModal}
                    data-dismiss="modal"
                  >
                    &times;
                  </button>
                </div>
                {children}
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

ModalWrapper.propTypes = {
  title: PropTypes.string,
  show: PropTypes.bool,
  onClose: PropTypes.func,
  children: PropTypes.node,
  lgModal: PropTypes.bool
};

ModalWrapper.defaultProps = {
  onClose: () => {},
  lgModal: false
};

export default ModalWrapper;
