//       getUserData(utype, { ...params });
//     }
//   }

//   validateForm = () => {
//     const [emailValidity, emailErrors] = checkEmail(
//       this.state[this.EMAIL_FIELD_ID],
//       'Email'
//     );
//     const formValidity = emailValidity;
//     const formErrors = { ...emailErrors };

//     this.setState({ formValidity, formErrors });

//     return formValidity;
//   };

static getDerivedStateFromProps(nextProps, prevState) {
    const { errorCode } = nextProps.errorCode;
    if (errorCode && errorCode !== prevState.errorCode) {
      const errMsg = getErrorResMsg(errorCode, PAGE.ADD_ORG_UNIT);
      return { formErrors: errMsg, errorCode };
    }
    return prevState;
  }

  enableSubmit = (orgUnitName, parent) => {
    const disabled = !(
     orgUnitName.length>0 
    //  && parent.length>0
    );
    this.setState({ disabled });
  };

  handleChange = event => {
    this.setState(
      {
        [event.target.id]: event.target.value,
        formErrors: {}
      },
      () =>
        this.enableSubmit(
          this.state[this.ORG_UNIT_FIELD_ID],
          this.state[this.PARENT_FIELD_ID]
        )
    );
  };

  handleParents = event => {
    this.setState(
      {
        [this.PARENT_FIELD_ID]: event || [],
        formErrors: {}
      },
      () =>
        this.enableSubmit(
          this.state[this.ORG_UNIT_FIELD_ID],
          this.state[this.PARENT_FIELD_ID]
        )
    );
  };

  handleSubmit = event => {
    event.preventDefault();
    this.setState({ errorCode: '' });
    this.props.reset({ type: ACTIONS.ORG_UNITS.RESET_STATE });
      const data = {
        name: this.state[this.ORG_UNIT_FIELD_ID],
        parent: 1,
      };
      const type = pageConfig[this.props.page].actions.postData;
      this.props.submit(type, { data, props: { ...this.props } });
  };

  render() {
    const { page } = this.props;
    const { pageTitle } = pageConfig[page];
    return (
      <div className="add-edit-user">
        <PageHeader title={pageTitle} />
        <AddEditOrgUnitsForm 
            handleSubmit= {this.handleSubmit}
            formErrors= {this.state.formErrors}
            orgUnitFieldId={this.ORG_UNIT_FIELD_ID}
            orgUnitFieldValue={this.state[this.ORG_UNIT_FIELD_ID]}
            handleParents={this.handleParents}
            parentFieldValues={this.state[this.PARENT_FIELD_ID]}
            parentFieldIds={this.PARENT_FIELD_ID}
            handleChange={this.handleChange}
            disabled={this.state.disabled}
        />
      </div>
    );
  }
}

AddEditOrgUnits.propTypes = {
  page: PropTypes.string.isRequired,
  match: PropTypes.object.isRequired,
  submit: PropTypes.func.isRequired,
  getParentDepartments: PropTypes.func.isRequired,
  parentOrgUnitsData: PropTypes.array.isRequired
};

AddEditOrgUnits.defaultProps = {};

const mapStateToProps = state => ({
  parentOrgUnitsData: state.addEditUserReducerStore.parentOrgUnitsData,
  errorCode: state.addEditUserReducerStore.errorCode
});

const mapDispatchToProps = dispatch => {
  return {
    getParentDepartments: (dtype, data) => {
      dispatch(getActionsFormate(dtype, data));
    },
    submit: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    reset: (type, data)=>{
        dispatch(getActionsFormate(type, data))
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(AddEditOrgUnits)
);
