import React from 'react';
import Tooltip from '@atlaskit/tooltip';
import Select, { components } from 'react-select';
import PropTypes from 'prop-types';

const Input = props => {
  // if (props.isHidden) {
    return <components.Input {...props} isDisabled={false} className="display" isHidden={false}/>;
  // }
  // return (
  //   <div>
  //     <Tooltip content="Custom Input" id="custom-inp"  className="pl-0">
  //       <components.Input id="custom-input" className="pl-0" {...props} />
  //     </Tooltip>
  //   </div>
  // );
};

const colourStyles = {
  control: styles => ({
    ...styles,
    backgroundColor: 'transparant',
    border: 'none',
    borderBottom: '1px solid #b9b9b9',
    borderRadius: '0px',
    width: '31.333333%'
  }),
  option: styles => ({ padding: '0px', ...styles }),
  input: styles => ({ padding: '0px', ...styles }),
  placeholder: styles => ({ padding: '0px', ...styles })
};

const CustomSelect = props => {
  return (
    <Select
      closeMenuOnSelect={false}
      components={{ Input }}
      onChange={props.onChange}
      options={props.fieldValues}
      isMulti
      placeholder={props.placeholder}
      styles={colourStyles}
      id="custom-select"
    />
  );
};
CustomSelect.propTypes = {
  onChange: PropTypes.func.isRequired,
  fieldValues: PropTypes.string.isRequired,
  placeholder: PropTypes.string.isRequired
};
Input.propTypes = {
  isHidden: PropTypes.bool.isRequired
};

export default CustomSelect;
