import React from 'react';
import PropTypes from 'prop-types';
import { faEnvelope } from '@fortawesome/free-regular-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { InputGroup, FormControl, Button } from 'react-bootstrap';

import DisplayErrors from './DisplayErrors';
import ButtonLoader from './ButtonLoader/ButtonLoader';
import { SUBMIT } from '../utils/constants';

const ForgotPasswordForm = props => {
  return (
    <div className="card-text text-left">
      Enter your email to reset the password.
      <div className="mt-4">
        <form onSubmit={props.handleSubmit} autoComplete="off">
          <DisplayErrors errors={props.formErrors} />
          <InputGroup>
            <InputGroup.Prepend>
              <InputGroup.Text className="pl-0">
                <FontAwesomeIcon icon={faEnvelope} />
              </InputGroup.Text>
            </InputGroup.Prepend>
            <FormControl
              type="email"
              placeholder="Email"
              id={props.emailFieldId}
              value={props.emailFieldValue}
              name={props.emailFieldName}
              onChange={props.handleChange}
            />
          </InputGroup>
          <CustomButton
            disabled={props.disabled}
            isLoading={props.inProgress}
            submitFunction={props.handleSubmit}
            id="forgot-password-button"
            text={LOGIN_BUTTON_TEXT}
          />
          {/* <Button
            block
            type="submit"
            className="btn onboarding-btn"
            disabled={props.disabled}
            id="forgot-password-button"
          >
            {props.inProgress && <ButtonLoader />}
            {!props.inProgress && SUBMIT}
          </Button> */}
        </form>
      </div>
    </div>
  );
};

ForgotPasswordForm.propTypes = {
  disabled: PropTypes.bool,
  handleSubmit: PropTypes.func.isRequired,
  formErrors: PropTypes.object.isRequired,
  emailFieldId: PropTypes.string.isRequired,
  emailFieldValue: PropTypes.string.isRequired,
  emailFieldName: PropTypes.string.isRequired,
  handleChange: PropTypes.func.isRequired,
  inProgress: PropTypes.bool.isRequired
};

ForgotPasswordForm.defaultProps = {
  disabled: true
};

export default ForgotPasswordForm;
