import { call, put } from 'redux-saga/effects';
import ACTIONS from '../../apiConfig/actions.constants';
import { FOOTPRINTS_API } from '../../apiConfig/api.config';
import { FOOTPRINTS_ROUTES } from '../../config';

function* addAssetAttributes(action) {
  let response;
  try {
    response = yield call(
      window.axios.postData,
      FOOTPRINTS_API.ASSET_ATTRIBUTES,
      action.data.data
    );

    if (response.status === 201 || response.status === 200) {
      action.data.props.history.push(FOOTPRINTS_ROUTES.ASSET_ATTRIBUTES.LIST);
      yield put({
        type: ACTIONS.ASSET_ATTRIBUTES.ADD_ASSET_ATTRIBUTES_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.ASSET_ATTRIBUTES.ADD_ASSET_ATTRIBUTES_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({
      type: ACTIONS.ASSET_ATTRIBUTES.ADD_ASSET_ATTRIBUTES_FAIL,
      payload: e
    });
  }
}

const dummy = {};

export { addAssetAttributes, dummy };
