import React from 'react';
import { Redirect } from 'react-router-dom';
import { setCookie, getCookie, deleteCookie } from './cookieFunctions';
import { userSessionCookie, rememberUserCookie, PAGE_ACCESS } from '../config';

const setSession = (data, cookieName) => {
  setCookie(cookieName, JSON.stringify(data), 1);
};

const redirectToLogin = () => {
  const urlParts = window.location.pathname.split('/');
  window.location.assign(`/${urlParts[0]}`);
};

const getSession = cookieName => {
  let sessionData;
  try {
    sessionData = JSON.parse(getCookie(cookieName)) || {};
  } catch (e) {
    sessionData = {};
    setSession(sessionData); // set it empty
  }
  return sessionData;
};

const deleteSession = cookieName => {
  deleteCookie(cookieName);
};

const deleteRememberMe = cookieName => {
  deleteCookie(cookieName);
};

const setRememberMe = (isRememberMe, token, cookieName) => {
  const data = { isRememberMe: true, ...token };
  if (isRememberMe) {
    setCookie(cookieName, JSON.stringify(data), 30);
  } else {
    deleteRememberMe(cookieName);
  }
};

const getRememberMe = cookieName => {
  let rememberMe;
  try {
    rememberMe = JSON.parse(getCookie(cookieName));
  } catch (e) {
    rememberMe = {};
  }
  return rememberMe;
};

const isUserLoggedIn = () => {
  const { sessionId, user } = getSession();
  if (user && sessionId) {
    return true;
  }
  return false;
};

const isAdmin = () => {
  const { user } = getSession();
  if (user && user.roles.filter(el => el.name === 'superAdmin').length > 0) {
    return true;
  }
  return false;
};

const logout = (sessionCookie, rememberMeCookie) => {
  deleteSession(sessionCookie);
  deleteRememberMe(rememberMeCookie);
  redirectToLogin();
};

const handleUrl = authorizedComponent => {
  return checkUserLoggedIn() ? authorizedComponent : <Redirect to="/login" />;
};

const checkUserLoggedIn = () => {
  const sessionData = getSession(userSessionCookie);
  const rememberData = getRememberMe(rememberUserCookie);
  if (
    (sessionData &&
      Object.entries(sessionData).length > 0 &&
      sessionData.token) ||
    (rememberData &&
      Object.entries(rememberData).length > 0 &&
      rememberData.token)
  ) {
    return true;
  }
  return false;
};

function checkUserValidity(page, role) {
  const sessionData = getSession(userSessionCookie);
  const rememberData = getSession(rememberUserCookie);
  let groups = [];
  if (sessionData) {
    groups = sessionData.groups;
  } else if (rememberData) {
    groups = rememberData.groups;
  }
  let validUser = false;
  if (groups) {
    for (let i = 0; i < groups.length; i += 1) {
      const groupName = groups[i] ? groups[i].name : false;
      if(page){
        validUser = isPageAccessible(groupName);
      }
      if(role){
        validUser = isUserRoleExist(groupName);
      }
      if(validUser){
        break;
      }
    }
  }
  return validUser;
}


function isPageAccessible(groupName, page){
  if (groupName && PAGE_ACCESS[groupName][0].includes(page)) {
    return true;
  }
  return false;
}

function isUserRoleExist(groupName){
  if(groupName && groupName === role){
    return true;
  }
  return false;
}

export {
  logout,
  isAdmin,
  handleUrl,
  setSession,
  getSession,
  setRememberMe,
  deleteSession,
  getRememberMe,
  isUserLoggedIn,
  redirectToLogin,
  checkUserLoggedIn,
  checkUserValidity
};
