import React from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import PageHeader from '../../components/PageHeader/PageHeader';
import pageConfig from '../../utils/pageConfig';
import { getActionsFormate } from '../../utils/common';
import ACTIONS from '../../apiConfig/actions.constants';
import { getErrorResMsg } from '../../utils/formValidations';
import { PAGE } from '../../utils/constants';
import AddEditAttributesForm from '../../components/AddEditAttributesForm/AddEditAttributesForm';

class AddEditAssetAttributes extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      formErrors: {},
      formValidity: false,
      errorCode: '',
      disabled: true
    };
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    const { errorCode } = nextProps;
    if (errorCode && errorCode !== prevState.errorCode) {
      const errMsg = getErrorResMsg(errorCode, PAGE.ASSET_ATTRIBUTES);
      return { formErrors: errMsg, errorCode };
    }
    return prevState;
  }

  enableSubmit = (docType, color, attribute, days, organization) => {
    const disabled = !(
      docType.length > 0 &&
      color.length > 0 &&
      attribute.length > 0 &&
      days.length > 0 &&
      organization.length > 0
    );
    if (this.props.errorCode) {
      const type = ACTIONS.ASSET_TYPES.RESET_STATE;
      this.props.resetState(type);
      this.setState({ errorCode: '' });
    }
    this.setState({ disabled });
  };

  handleChange = event => {
    this.setState(
      {
        [event.target.id]: event.target.value,
        formErrors: {}
      },
      () =>
        this.enableSubmit(
          this.state[this.ASSET_TYPE_FIELD_ID],
          this.state[this.COLOR_FIELD_ID],
          this.state[this.ATTRIBUTE_FIELD_ID],
          this.state[this.DAYS_FIELD_ID],
          this.state[this.ORGANISATION_FIELD_ID]
        )
    );
  };

  handleSubmit = event => {
    event.preventDefault();
    const data = {
      doc_type: this.state[this.ASSET_TYPE_FIELD_ID],
      color: this.state[this.COLOR_FIELD_ID],
      attribute: this.state[this.ATTRIBUTE_FIELD_ID],
      days_to_process: this.state[this.COLOR_FIELD_ID],
      organisation: this.state[this.ORGANISATION_FIELD_ID]
    };
    const type = pageConfig[this.props.page].actions.postData;
    this.props.submit(type, { data, props: { ...this.props } });
  };

  render() {
    const { page } = this.props;
    const { pageTitle } = pageConfig[page];

    return (
      <div className="add-edit-user">
        <PageHeader title={pageTitle} />
        <AddEditAttributesForm
          handleSubmit={this.handleSubmit}
          formErrors={this.state.formErrors}
          handleChange={this.handleChange}
          disabled={this.state.disabled}
          organizationFieldValue={this.state[this.ORGANISATION_FIELD_ID]}
          organizationFieldId={this.ORGANISATION_FIELD_ID}
          assetTypeFieldId={this.ASSET_TYPE_FIELD_ID}
          assetTypeFieldValue={this.state[this.ASSET_TYPE_FIELD_ID]}
          colorFieldId={this.COLOR_FIELD_ID}
          colorFieldValue={this.state[this.COLOR_FIELD_ID]}
          attributeFieldId={this.ATTRIBUTE_FIELD_ID}
          attributeFieldValue={this.state[this.ATTRIBUTE_FIELD_ID]}
          daysFieldId={this.DAYS_FIELD_ID}
          daysFieldValue={this.state[this.DAYS_FIELD_ID]}
        />
      </div>
    );
  }
}

AddEditAssetAttributes.propTypes = {
  page: PropTypes.string.isRequired,
  submit: PropTypes.func.isRequired,
  errorCode: PropTypes.string.isRequired,
  resetState: PropTypes.func.isRequired
};

AddEditAssetAttributes.defaultProps = {};

const mapStateToProps = state => ({
  errorCode: state.addEditAssetTypes.errorCode
});

const mapDispatchToProps = dispatch => {
  return {
    submit: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    resetState: type => {
      dispatch(getActionsFormate(type));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(AddEditAssetAttributes)
);
