import React from 'react';
import PropTypes from 'prop-types';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faObjectGroup, faBuilding } from '@fortawesome/free-regular-svg-icons';

import {
  faBars,
  faAngleDoubleLeft,
  faAngleDoubleRight,
  faChartLine,
  faUsers
} from '@fortawesome/free-solid-svg-icons';
import { withRouter, Link, NavLink } from 'react-router-dom';
import BreadCrumb from '../BreadCrumb/BreadCrumb';
import './SideNav.scss';
import { PAGE } from '../../utils/constants';
import { FOOTPRINTS_ROUTES } from '../../config';

class SideNav extends React.Component {
  constructor(props) {
    super(props);
    let path = this.props.history.location.pathname;
    path = `/${path.split('/')[1]}`;
    const index = Object.values(FOOTPRINTS_ROUTES).indexOf(path);
    const activePage = Object.keys(FOOTPRINTS_ROUTES)[index];
    this.state = {
      activePage
    };
  }

  setActivePage = activePage => {
    this.props.history.push(FOOTPRINTS_ROUTES[activePage]);
    this.setState({ activePage });
  };

  render() {
    const { activePage } = this.state;
    const { children } = this.props;
    const sidebarId = 'sidebar';
    const contentId = 'content';
    const expandClassName = 'expanded';

    const toggleSidebar = event => {
      event.preventDefault();
      const sidebar = document.getElementById(sidebarId);
      sidebar.classList.toggle(expandClassName);
    };

    const hideSidebar = event => {
      event.preventDefault();
      const sidebar = document.getElementById(sidebarId);
      sidebar.classList.toggle(expandClassName);
    };

    return (
      <div className="sidenav">
        <nav className="sidebar" id={sidebarId}>
          <ul className="list-unstyled components">
            <div
              className="sidebar-dismiss"
              role="button"
              onClick={hideSidebar}
            >
              <FontAwesomeIcon icon={faAngleDoubleLeft} size="lg" />
            </div>
            <div className="header">Org Unit Name</div>
            <div
              className="header-small-width"
              onClick={toggleSidebar}
              role="button"
            >
              <FontAwesomeIcon
                id="arrow-expand"
                icon={faAngleDoubleRight}
                size="lg"
              />
            </div>
            <NavLink
              className="nav-link"
              strict
              to={FOOTPRINTS_ROUTES.ANALYTICS}
              activeClassName="selected"
            >
              <FontAwesomeIcon icon={faChartLine} size="lg" />
              <span className="sidenav-item-text">Analytics</span>
            </NavLink>

            <NavLink
              className="nav-link"
              strict
              to={FOOTPRINTS_ROUTES.ORG_UNITS}
              activeClassName="selected"
            >
              <FontAwesomeIcon icon={faBuilding} size="lg" />
              <span className="sidenav-item-text">Org Units</span>
            </NavLink>
            <NavLink
              className="nav-link"
              strict
              to={FOOTPRINTS_ROUTES.ASSETS_TYPE}
              activeClassName="selected"
            >
              <FontAwesomeIcon icon={faObjectGroup} size="lg" />
              <span className="sidenav-item-text">Asset Types</span>
            </NavLink>
            <NavLink
              className="nav-link"
              strict
              to={FOOTPRINTS_ROUTES.USERS}
              activeClassName="selected"
            >
              <FontAwesomeIcon icon={faUsers} size="lg" />
              <span className="sidenav-item-text">Users</span>
            </NavLink>
          </ul>
        </nav>

        <div className="content" id={contentId}>
          <div className="container">
            <nav className="navbar navbar-expand-lg breadcrumbs-nav">
              <button
                type="button"
                id="sidebarCollapse"
                onClick={toggleSidebar}
                className="icon-btn sidebar-collapse-btn"
              >
                <FontAwesomeIcon icon={faBars} size="lg" />
              </button>
              <BreadCrumb />
            </nav>
            {children}
          </div>
        </div>
      </div>
    );
  }
}

SideNav.propTypes = {
  children: PropTypes.object.isRequired,
  history: PropTypes.object.isRequired
};

export default withRouter(SideNav);
