import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import pageConfig from '../../utils/pageConfig';
import PageLoader from '../../components/PageLoader/PageLoader';
import PageHeader from '../../components/PageHeader/PageHeader';
import Table from '../../components/Table/Table';
import { getActionsFormate } from '../../utils/common';
import { onPagination, onPageChange } from '../../utils/utils';
import { reverse } from 'named-urls';

class Assets extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      perPage: 10,
      currentPage: 1,
      start: 1,
      end: 5
    };
    this.onPagination = onPagination.bind(this);
    this.onPageChange = onPageChange.bind(this);
  }

  componentDidMount() {
    const { getAssetsList, page } = this.props;
    const { actions } = pageConfig[page];
    const type = actions && actions.fetchList;
    getAssetsList(type, { limit: this.state.perPage, offset: 10 });
  }

  componentDidUpdate(prevProps, prevState) {
    if (this.state.currentPage !== prevState.currentPage) {
      const { getAssetsList, page } = this.props;
      const { actions } = pageConfig[page];
      const type = actions && actions.fetchList;
      const offset = (this.state.currentPage - 1) * 10;
      getAssetsList(type, {
        limit: this.state.perPage,
        offset
      });
    }
  }

  showAssetDetails = QrId => {
    const {history} = this.props;
    const url = reverse(FOOTPRINTS_ROUTES.ASSET.DETAIL.SHOW, { QrId });
    history.push(url);
    return true;
  };

  render() {
    const { page, inProgress, Data } = this.props;
    const { pageTitle } = pageConfig[page];
    const Theader = (
      <tr>
        <th>Asset</th>
        <th>Asset Type</th>
        <th>Submitted Date</th>
        <th>Due Date</th>
        <th>Status</th>
      </tr>
    );
    let customKey = 0;
    const Tbody =
      Data &&
      Data.results &&
      Data.results.length > 0 &&
      Data.results.map(each => {
        const {
          asset_type: AssetType,
          due_date: DueDate,
          qr_id: QrId,
          state: Status,
          submitted_date: SubmittedDate
        } = each;
        const submitedDate = SubmittedDate || '-';
        customKey += 1;
        return (
          <tr
            key={customKey}
            onClick={eve => {
              this.showAssetDetails(QrId);
            }}
          >
            <td id="QrId">{QrId.slice(0, 7)}</td>
            <td id="asset-type">{AssetType}</td>
            <td id="submitted-date">{submitedDate}</td>
            <td id="due-date">{DueDate || '-'}</td>
            <td id="status">{Status}</td>
          </tr>
        );
      });

    const paginationData = {
      start: this.state.start,
      end: this.state.end,
      currentPage: this.state.currentPage,
      totalPages: Data ? Data.count / this.state.perPage : 0,
      onPagination: this.onPagination,
      onPageChange: this.onPageChange
    };

    return (
      <div>
        <PageLoader inProgress={inProgress} />
        <PageHeader {...this.props} title={pageTitle} />
        <Table header={Theader} body={Tbody} paginationData={paginationData} />
      </div>
    );
  }
}

Assets.propTypes = {
  getAssetsList: PropTypes.func.isRequired,
  page: PropTypes.string.isRequired,
  match: PropTypes.object.isRequired,
  inProgress: PropTypes.bool.isRequired,
  Data: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  Data: state.AssetsReducerStore.data,
  inProgress: state.AssetsReducerStore.inProgress
});

const mapDispatchToProps = dispatch => {
  return {
    getAssetsList: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    resetState: type => {
      dispatch(getActionsFormate(type));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(Assets)
);
