import React from 'react';
import PropTypes from 'prop-types';
import { faEllipsisV } from '@fortawesome/free-solid-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

import './PageHeader.scss';

const PageHeader = props => {
  const actions = props.actions.map((actionObject, index) => (
    <button
      key={index}
      className="btn btn-sm btn-primary"
      onClick={actionObject.handler}
    >
      {actionObject.text}
    </button>
  ));

  const actionComponent =
    actions.length > 0 ? (
      <>
        <div
          key="actionButton"
          className="col col-md-7 d-none d-md-block text-right"
        >
          {actions}
        </div>
        <div key="actionEllipsis" className="col col-2 d-md-none text-right">
          <div className="options-btn" role="button">
            {/* <FontAwesomeIcon icon={faEllipsisV} /> */}
          </div>
        </div>
      </>
    ) : null;

  return (
    <div className="page-header">
      <div className="row align-items-center">
        <div className="col col-xs-10 col-md-5">
          <h4 className="page-title">{props.title}</h4>
        </div>
        {actionComponent}
      </div>
    </div>
  );
};

PageHeader.propTypes = {
  actions: PropTypes.array,
  title: PropTypes.string.isRequired
};

PageHeader.defaultProps = {
  actions: []
};

export default PageHeader;
