import { call, put } from 'redux-saga/effects';

import { reverse } from 'named-urls';
import ACTIONS from '../../apiConfig/actions.constants';
import { FOOTPRINTS_API } from '../../apiConfig/api.config';

function* getUserList(action) {
  let response;
  try {
    response = yield call(window.axios.getData, FOOTPRINTS_API.USER);

    if (response.status === 200) {
      yield put({
        type: ACTIONS.USERS.GET_USER_LIST_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.USERS.GET_USER_LIST_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({ type: ACTIONS.USERS.GET_USER_LIST_FAIL, payload: e });
  }
}

function* getOrgUnitsList(action) {
  let response;
  try {
    response = yield call(
      window.axios.getData,
      FOOTPRINTS_API.ORG_UNITS,
      action.data
    );
    if (response.status === 200) {
      yield put({
        type: ACTIONS.ORG_UNITS.GET_ORG_UNITS_LIST_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.ORG_UNITS.GET_ORG_UNITS_LIST_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({ type: ACTIONS.ORG_UNITS.GET_ORG_UNITS_LIST_FAIL, payload: e });
  }
}

function* getOrgDetails(action) {
  let response;
  console.log("urls", action.data);

  try {
    response = yield call(
      window.axios.getData,
      reverse(FOOTPRINTS_API.ORG_UNITS_DETAILS, {...action.data}),
      action.data
    );
    console.log("Response:", response);

    if (response.status === 200) {
      yield put({
        type: ACTIONS.ORG_UNITS.GET_ORG_UNITS_DETAILS_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.ORG_UNITS.GET_ORG_UNITS_DETAILS_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({
      type: ACTIONS.ORG_UNITS.GET_ORG_UNITS_DETAILS_FAIL,
      payload: e
    });
  }
}

function* getAssetAttributes(action) {
  let response;
  try {
    response = yield call(
      window.axios.getData,
      FOOTPRINTS_API.ASSET_ATTRIBUTES,
      action.data
    );

    if (response.status === 200) {
      yield put({
        type: ACTIONS.ASSET_ATTRIBUTES.GET_ASSET_ATTRIBUTES_SUCCESS,
        payload: response.data.attributes
      });
    } else {
      yield put({
        type: ACTIONS.ASSET_ATTRIBUTES.GET_ASSET_ATTRIBUTES_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({
      type: ACTIONS.ASSET_ATTRIBUTES.GET_ASSET_ATTRIBUTES_FAIL,
      payload: e
    });
  }
}

function* getAssetTypes(action) {
  let response;
  try {
    response = yield call(
      window.axios.getData,
      FOOTPRINTS_API.ASSET_TYPES,
      action.data
    );
    if (response.status === 200) {
      yield put({
        type: ACTIONS.ASSET_TYPES.GET_ASSET_TYPES_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.ASSET_TYPES.GET_ASSET_TYPES_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({
      type: ACTIONS.ASSET_TYPES.GET_ASSET_TYPES_FAIL,
      payload: e
    });
  }
}

function* getAllOrgUnit(action) {
  let response;
  try {

    let url = FOOTPRINTS_API.ORG_UNIT_ALL + "?flat_list=false";
    response = yield call(
      window.axios.getData,
      url,
      action.data
    );
    if (response.status === 200) {
      yield put({
        type: ACTIONS.ORG_UNITS.GET_ALL_ORG_UNIT_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.ORG_UNITS.GET_ALL_ORG_UNIT_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({
      type: ACTIONS.ORG_UNITS.GET_ALL_ORG_UNIT_FAIL,
      payload: e
    });
  }
}

export {
  getUserList,
  getOrgUnitsList,
  getOrgDetails,
  getAssetAttributes,
  getAssetTypes,
  getAllOrgUnit
};
