import { call, put } from 'redux-saga/effects';

import { faRestroom } from '@fortawesome/free-solid-svg-icons';
import ACTIONS from '../../apiConfig/actions.constants';
import { FOOTPRINTS_API } from '../../apiConfig/api.config';
import { FOOTPRINTS_ROUTES } from '../../config';

function* createUser(action) {
  console.log('Actions:', action);

  let response;
  try {
    response = yield call(
      window.axios.postData,
      FOOTPRINTS_API.USER,
      action.data.data
    );

    console.log('responese:', response.status === 200, action.data.props);
    if (response.status === 200) {
      action.data.props.history.push(FOOTPRINTS_ROUTES.USERS.LIST);
      // yield put({
      //   type:ACTIONS.USERS.GET_USER_LIST
      // })
      yield put({
        type: ACTIONS.USERS.ADD_USER_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.USERS.ADD_USER_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({ type: ACTIONS.USERS.ADD_USER_FAIL, payload: e });
  }
}

function* updateUser(action) {
  let response;
  try {
    response = yield call(
      window.axios.putData,
      FOOTPRINTS_API.USER,
      action.data
    );
    if (response.status === 200) {
      yield put({
        type: ACTIONS.USERS.UPDATE_USER_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.USERS.UPDATE_USER_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({ type: ACTIONS.USERS.UPDATE_USER_FAIL, payload: e });
  }
}

function* getUserDepartments(action) {
  let response;
  try {
    const URL = `${FOOTPRINTS_API.DEPARTMENTS}?flat_list=true`;
    response = yield call(window.axios.getData, URL);

    if (response.status === 200) {
      console.log('response:', response);
      yield put({
        type: ACTIONS.USERS.GET_USER_DEPARTMENTS_SUCCESS,
        payload: response.data
      });
    } else {
      yield put({
        type: ACTIONS.USERS.GET_USER_DEPARTMENTS_FAIL,
        payload: response.data
      });
    }
  } catch (e) {
    yield put({ type: ACTIONS.USERS.GET_USER_DEPARTMENTS_FAIL, payload: e });
  }
}
export { createUser, updateUser, getUserDepartments };
