import React from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import AddEditUserForm from '../../components/AddEditUserForm/AddEditUserForm';
import { checkEmail, getErrorResMsg } from '../../utils/formValidations';
import PageHeader from '../../components/PageHeader/PageHeader';
import pageConfig from '../../utils/pageConfig';
import { getActionsFormate, getOrgUnitIds } from '../../utils/common';
import { STORE, ROLES, PAGE } from '../../utils/constants';
import ACTIONS from '../../apiConfig/actions.constants';

class AddEditUser extends React.Component {
  FIRST_NAME_FIELD_ID = 'aeu-fname';

  LAST_NAME_FIELD_ID = 'aeu-lname';

  EMAIL_FIELD_ID = 'aeu-email';

  DEPARTMENTS_FIELD_ID = 'select-departments';

  USER_ROLES_ID = 'user-roles';

  constructor(props) {
    super(props);
    this.state = {
      formErrors: {},
      formValidity: false,
      [this.FIRST_NAME_FIELD_ID]: '',
      [this.LAST_NAME_FIELD_ID]: '',
      [this.EMAIL_FIELD_ID]: '',
      [this.DEPARTMENTS_FIELD_ID]: [],
      [this.USER_ROLES_ID]: [],
      errorCode: '',
      disabled: true,
      departmentMode : 'multiSelect'
    };
  }

  componentDidMount() {
    const { page, getUserData, match } = this.props;
    const utype = pageConfig[page].actions.fetchUser;
    if (utype) {
      const { params } = match;
      getUserData(utype, { ...params });
    }
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    const { errorCode } = nextProps;
    if (errorCode && errorCode !== prevState.errorCode) {
      const errMsg = getErrorResMsg(errorCode, PAGE.ADD_USER);
      return { formErrors: errMsg, errorCode };
    }
    return prevState;
  }

  validateForm = () => {
    const [emailValidity, emailErrors] = checkEmail(
      this.state[this.EMAIL_FIELD_ID],
      'Email'
    );
    const formValidity = emailValidity;
    const formErrors = { ...emailErrors };

    this.setState({ formValidity, formErrors });

    return formValidity;
  };

  enableSubmit = (email, firstName, lastName, departments, userRoles) => {
    const disabled = !(
      email.length > 0 &&
      firstName.length > 0 &&
      lastName.length > 0 &&
      departments.length > 0 &&
      userRoles.length > 0
    );

    if (this.props.errorCode) {
      const type = ACTIONS.USERS.RESET_STATE;
      this.props.resetState(type);
      this.setState({ errorCode: '' });
    }
    this.setState({ disabled });
  };

  handleChange = event => {
    this.setState(
      {
        [event.target.id]: event.target.value,
        formErrors: {}
      },
      () =>
        this.enableSubmit(
          this.state[this.EMAIL_FIELD_ID],
          this.state[this.FIRST_NAME_FIELD_ID],
          this.state[this.LAST_NAME_FIELD_ID],
          this.state[this.DEPARTMENTS_FIELD_ID],
          this.state[this.USER_ROLES_ID]
        )
    );
  };


  handleOrgUnit = (currentNode, selectedNodes) => {
      this.setState(
      {
        [this.DEPARTMENTS_FIELD_ID]: getOrgUnitIds(selectedNodes),
        formErrors: {}
      },
      () =>
        this.enableSubmit(
          this.state[this.EMAIL_FIELD_ID],
          this.state[this.FIRST_NAME_FIELD_ID],
          this.state[this.LAST_NAME_FIELD_ID],
          this.state[this.DEPARTMENTS_FIELD_ID],
          this.state[this.USER_ROLES_ID]
        )
    );


  };

  handleRoles = event => {
    this.setState(
      {
        [this.USER_ROLES_ID]: event || [],
        formErrors: {}
      },
      () =>
        this.enableSubmit(
          this.state[this.EMAIL_FIELD_ID],
          this.state[this.FIRST_NAME_FIELD_ID],
          this.state[this.LAST_NAME_FIELD_ID],
          this.state[this.DEPARTMENTS_FIELD_ID],
          this.state[this.USER_ROLES_ID]
        )
    );
  };

  getUserRoleIds = userRoles => {
    const userRolesIds = userRoles.map(role => {
      return ROLES.indexOf(role);
    });
    return userRolesIds;
  };

  getRolesFormate = () => {
    const userRoles = ROLES.map(role => {
      return { value: role, label: role };
    });
    return userRoles;
  };

  handleSubmit = event => {
    event.preventDefault();
    if (this.validateForm()) {
      const data = {
        email: this.state[this.EMAIL_FIELD_ID],
        first_name: this.state[this.FIRST_NAME_FIELD_ID],
        last_name: this.state[this.LAST_NAME_FIELD_ID],
        department: this.state[this.DEPARTMENTS_FIELD_ID],
        user_roles: this.getUserRoleIds(this.state[this.USER_ROLES_ID])
      };
      const type = pageConfig[this.props.page].actions.postData;
      this.props.submit(type, { data, props: { ...this.props } });
    }
  };

  render() {
    const { page } = this.props;
    const { pageTitle } = pageConfig[page];
    return (
      <div className="add-edit-user">
        <PageHeader title={pageTitle} />
        <AddEditUserForm
          handleSubmit={this.handleSubmit}
          handleChange={this.handleChange}
          handleOrgUnit={this.handleOrgUnit}
          firstNameFieldId={this.FIRST_NAME_FIELD_ID}
          lastNameFieldId={this.LAST_NAME_FIELD_ID}
          emailFieldId={this.EMAIL_FIELD_ID}
          firstNameFieldValue={this.state[this.FIRST_NAME_FIELD_ID]}
          lastNameFieldValue={this.state[this.LAST_NAME_FIELD_ID]}
          emailFieldValue={this.state[this.EMAIL_FIELD_ID]}
          departmentsFieldValue={this.props.userDepartments}
          departmentsFieldId={this.DEPARTMENTS_FIELD_ID}
          handleChangeRoles={this.handleRoles}
          userRolesId={this.USER_ROLES_ID}
          userRoleValues={this.getRolesFormate()}
          formErrors={this.state.formErrors}
          disabled={this.state.disabled}
          page={page}
          mode={this.state.departmentMode}
        />
      </div>
    );
  }
}

AddEditUser.propTypes = {
  page: PropTypes.string.isRequired,
  match: PropTypes.object.isRequired,
  getUserData: PropTypes.func.isRequired,
  submit: PropTypes.func.isRequired,
  errorCode: PropTypes.oneOfType([PropTypes.string, PropTypes.number])
    .isRequired,
  resetState: PropTypes.func.isRequired
};

AddEditUser.defaultProps = {};

const mapStateToProps = state => ({
  errorCode: state.addEditUserReducerStore.errorCode,
  userData: state.DetailsReducerStore.data,
});

const mapDispatchToProps = dispatch => {
  return {
    submit: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    getUserData: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    resetState: type => {
      dispatch(getActionsFormate(type));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(AddEditUser)
);
