import React from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import 'react-dropdown-tree-select/dist/styles.css';
import './CustomTreeSelect.scss';
import DropdownTreeSelect from 'react-dropdown-tree-select';
import pageConfig from '../../utils/pageConfig';
import { STORE } from '../../utils/constants';
import { getActionsFormate } from '../../utils/common';

class CustomTreeSelect extends React.Component {
  componentDidMount() {
    const { getData, page } = this.props;
    const dtype = pageConfig[page].actions.getAllOrgUnit;
    getData(dtype);
  }

  render() {
    const {
      allOrgUnit,
      onChange,
      mode,
      defaultValues,
      page,
      placeholder
    } = this.props;
    const getFormate = pageConfig[page].formate;
    return (
      <div>
        <div className="custom-drop-down ">
          <div className="row">
            <DropdownTreeSelect
              texts={{ placeholder }}
              data={getFormate(allOrgUnit, defaultValues)}
              onChange={onChange}
              mode={mode}
              id="custom-tree-select"
              className={
                allOrgUnit.length > 0 ? 'col col-10' : 'no-data col col-10'
              }
            />
          </div>
        </div>
      </div>
    );
  }
}

CustomTreeSelect.propTypes = {
  page: PropTypes.string.isRequired,
  getData: PropTypes.func.isRequired,
  allOrgUnit: PropTypes.array.isRequired,
  onChange: PropTypes.func.isRequired,
  mode: PropTypes.string.isRequired,
  defaultValues: PropTypes.array.isRequired,
  placeholder: PropTypes.string.isRequired
};

const mapStateToProps = (state, props) => {
  return {
    errorCode: state.addEditOrgUnitReducerStore.errorCode,
    allOrgUnit: state.commonReducerStore[STORE.ALL_ORG_UNIT]
  };
};

const mapDispatchToProps = dispatch => {
  return {
    getData: type => {
      dispatch(getActionsFormate(type));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(CustomTreeSelect)
);
