import React from 'react';
import PropTypes from 'prop-types';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import ERROR_CODES from '../../utils/errorCodes';
import ACTIONS from '../../apiConfig/actions.constants';
import { ALERT_MESSAAGEs } from '../../utils/constants';
class AlertMessage extends React.Component {
    // constructor(props){
    //     super(props);
    //     this.state = {
    //         errorCode
    //     }
    // }
    notify = (status, errorCode) =>{
        switch(status){
            case ACTIONS.ALERT_MESSAGE.ADD_SUCCESS: toast.success(ALERT_MESSAAGEs[ACTIONS.ALERT_MESSAGE.ADD_SUCCESS], {
                                                        position: toast.POSITION.TOP_RIGHT
                                                    });
                                                    break;
            case ACTIONS.ALERT_MESSAGE.UPDATE_SUCCESS: toast.success(ALERT_MESSAAGEs[ACTIONS.ALERT_MESSAGE.ADD_SUCCESS], {
                                                        position: toast.POSITION.TOP_RIGHT
                                                    });
                                                    break;

            case ACTIONS.ALERT_MESSAGE.FAIL: toast.error(ERROR_CODES[errorCode], {
                                                position: toast.POSITION.TOP_CENTER,
                                                className: "w-100"
                                            });
                                            break;
        }

    };
    render() {
        const { status, errorCode} = this.props;
        return (
            <div>
                {this.notify(status, errorCode)}
                <ToastContainer />
            </div>
        )
    }
}

AlertMessage.propTypes = {
    status: PropTypes.string.isRequired,
    errorCode: PropTypes.number.isRequired
};

export default AlertMessage;
