import React from 'react';
import Tooltip from '@atlaskit/tooltip';
import Select, { components } from 'react-select';
import PropTypes from 'prop-types';

const Input = props => {
  if (props.isHidden) {
    return <components.Input {...props} />;
  }
  return (
    <div>
      <Tooltip content="Custom Input" className="pl-0">
        <components.Input className="pl-0" {...props} />
      </Tooltip>
    </div>
  );
};

const colourStyles = {
  control: styles => ({
    ...styles,
    backgroundColor: 'transparant',
    border: 'none',
    borderBottom: '1px solid #b9b9b9',
    borderRadius: '0px',
    width: '31.333333%'
  }),
  option: styles => ({ padding: '0px', ...styles }),
  input: styles => ({ padding: '0px', ...styles }),
  placeholder: styles => ({ padding: '0px', ...styles })
};

const CustomSelect = props => {
  console.log("defaultValue",props.defaultValue, props.fieldValues);
  const values = props.defaultValue.length>0 ? [{value: 2, label: "Asset Manager"}, {value: 3, label: "Organisation Unit Admin"}] : [];

  return (
    <Select
      // defaultValue={values}
      value={props.defaultValue}
      closeMenuOnSelect={false}
      components={{ Input }}
      onChange={props.onChange}
      options={props.fieldValues}
      isMulti
      placeholder={props.placeholder}
      styles={colourStyles}
      id="custom-select"
    />
  );
};
CustomSelect.propTypes = {
  onChange: PropTypes.func.isRequired,
  fieldValues: PropTypes.string.isRequired,
  placeholder: PropTypes.string.isRequired
};
Input.propTypes = {
  isHidden: PropTypes.bool.isRequired
};

export default CustomSelect;
