import React from 'react';
import Tooltip from '@atlaskit/tooltip';
import Select, { components } from 'react-select';
import PropTypes from 'prop-types';

const Input = props => {
  if (props.isHidden) {
    return <components.Input className="col col-4" {...props} />;
  }
  return (
    <div>
      <Tooltip content="Custom Input"  className="col col-4">
        <components.Input {...props} />
      </Tooltip>
    </div>
  );
};

const colourStyles = {
  control: styles => ({
    ...styles,
    backgroundColor: 'transparant',
    border: 'none',
    borderBottom: '1px solid #b9b9b9',
    borderRadius: '0px',
    width:"100%",

  }),
  option: styles => ({  padding: '0px', ...styles }),
  input: styles => ({padding: '0px', ...styles }),
  placeholder: styles => ({ padding: '0px', ...styles })
};

const CustomSelect = props => {
  return (
    <Select
      closeMenuOnSelect={false}
      components={{ Input }}
      onChange={props.onChange}
      options={props.departmentsFieldValue}
      isMulti
      placeholder="Roles"
      styles={colourStyles}
      className="col col-3"
    />
  );
};
CustomSelect.propTypes = {
  onChange: PropTypes.func.isRequired,
  departmentsFieldValue: PropTypes.string.isRequired
};
Input.propTypes = {
  isHidden: PropTypes.bool.isRequired
};
CustomSelect.defaultProps = {};

export default CustomSelect;
