import React from 'react';
import { InputGroup, FormControl, Button } from 'react-bootstrap';
import PropTypes from 'prop-types';
import DisplayErrors from '../DisplayErrors';
import 'react-dropdown-tree-select/dist/styles.css';
import './AddEditUserForm.scss';
import CustomSelect from '../CustomSelect/CustomSelect';
import CustomTreeSelect from '../CustomTreeSelect/CustomTreeSelect';
import { PAGE, SUBMIT } from '../../utils/constants';
import ButtonLoader from '../ButtonLoader/ButtonLoader';

const AddEditUserForm = props => {
  return (
    <div className=" card-text text-left add-edit-user">
      <div className="card p-3 pb-4">
        <form autoComplete="off">
          <DisplayErrors errors={props.formErrors} />
          <div className="row mb-2 ">
            <div className="col col-lg-4 col-12">
              <InputGroup size="sm">
                <FormControl
                  type="email"
                  placeholder="Email"
                  id={props.emailFieldId}
                  value={props.emailFieldValue}
                  name={props.emailFieldId}
                  onChange={props.handleChange}
                  readOnly={props.page === PAGE.UPDATE_USER}
                />
              </InputGroup>
            </div>
            <div className="col col-0 col-lg-4" />
          </div>
          <div className="row">
            <div className="col col-lg-4 col-12">
              <InputGroup size="sm">
                <FormControl
                  type="text"
                  placeholder="First Name"
                  id={props.firstNameFieldId}
                  value={props.firstNameFieldValue}
                  name={props.firstNameFieldId}
                  onChange={props.handleChange}
                />
              </InputGroup>
            </div>
            <div className="col col-lg-4 col-12">
              <InputGroup size="sm">
                <FormControl
                  type="text"
                  placeholder="Last Name"
                  id={props.lastNameFieldId}
                  value={props.lastNameFieldValue}
                  name={props.lastNameFieldId}
                  onChange={props.handleChange}
                />
              </InputGroup>
            </div>
          </div>
          <div className="row mb-4">
            <div className="col col-lg-4 col-12 ml-0">
              <CustomTreeSelect
                onChange={props.handleOrgUnit}
                page={props.page}
                mode={props.mode}
                defaultValues={props.selectedOrgUnits}
                placeholder="Departments"
              />
            </div>
          </div>
          <div className="row mb-4">
            <div className="col col-lg-4 col-12 ml-0">
              <CustomSelect
                onChange={props.handleChangeRoles}
                fieldValues={props.userRoleValues}
                fieldId={props.userRolesId}
                placeholder="Roles"
                values={props.existedValues}
              />
            </div>
          </div>
        </form>
      </div>
      <Button
        type="submit"
        className="btn onboarding-btn"
        disabled={props.disabled || props.inProgress}
        id="add-edit-submit-btn"
        onClick={props.handleSubmit}
      >
        {props.inProgress && <ButtonLoader />}
        {!props.inProgress && SUBMIT}
      </Button>
    </div>
  );
};

AddEditUserForm.propTypes = {
  handleSubmit: PropTypes.func,
  formErrors: PropTypes.object,
  emailFieldId: PropTypes.string,
  emailFieldValue: PropTypes.string,
  handleChange: PropTypes.func,
  firstNameFieldId: PropTypes.string,
  firstNameFieldValue: PropTypes.string,
  lastNameFieldId: PropTypes.string,
  lastNameFieldValue: PropTypes.string,
  handleOrgUnit: PropTypes.func,
  disabled: PropTypes.bool,
  handleChangeRoles: PropTypes.func,
  userRoleValues: PropTypes.array,
  userRolesId: PropTypes.string,
  mode: PropTypes.string,
  selectedOrgUnits: PropTypes.array,
  page: PropTypes.string,
  inProgress: PropTypes.bool,
  existedValues: PropTypes.array
};

AddEditUserForm.defaultProps = {};

export default AddEditUserForm;
