import React from 'react';
import './OrgUnitDetails.scss';
import PropTypes from 'prop-types';
import DataCards from '../DataCards/DataCards';
import { getOrgUnitsNext, getOrgUnits } from '../../utils/utils';
// import { connect } from 'react-redux';
// import { withRouter } from 'react-router-dom';
const OrgUnitDetails = (props)=>{
    // {"id":4,"name":"MobIux","location":"Bengaluru","parent":null,"child":[{"id":14,"name":"Childred","location":null,"has_children":true}]}
    const { Data, actions } = props;
    const {location, parent, child} = Data;
    const cardData = getOrgUnits(child);
    console.log("card Data", cardData)
    return (
        <div>

            <div className="row org-unit-location mb-2">
                <span className="text-capitalize  col col-3 org-unit-details-key">location</span>
                <span className="">:</span>
                <span className="text-capitalize  col org-unit-details-value">{location}</span>
            </div>
            <div className="row org-unit-parent mb-2">
                <span className="text-capitalize  col col-3 org-unit-details-key">parent</span>
                <span className="">:</span>
                <span className="text-capitalize  col org-unit-details-value">{parent ? parent.name: '-'}</span>
            </div>
            {cardData.length > 0 && <div>
                <div className="mb-3 mt-3">
                    <span className="org-unit-children-title"> Org Units</span>
                </div>
                <div>
                <DataCards {...props} data={cardData} actions={actions} />
                </div>
            </div>}
        </div>
    )
}

OrgUnitDetails.propTypes =  {

}

export default OrgUnitDetails;
