import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { reverse } from 'named-urls';
import pageConfig from '../../utils/pageConfig';
import PageLoader from '../../components/PageLoader/PageLoader';
import PageHeader from '../../components/PageHeader/PageHeader';
import Table from '../../components/Table/Table';
import { getActionsFormate, getFormatedDate } from '../../utils/common';
import {
  onPagination,
  onPageChange,
  getBodyComponenet
} from '../../utils/utils';
import { DATA_NOT_FOUND } from '../../utils/constants';
import ACTIONS from '../../apiConfig/actions.constants';
import CustomList from '../../components/CustomList/CustomList';

class Todos extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      perPage: 10,
      currentPage: 1,
      start: 1,
      end: 5
    };
    this.onPagination = onPagination.bind(this);
    this.onPageChange = onPageChange.bind(this);
  }

  componentDidMount() {
    this.fetchTodos();
  }

  componentDidUpdate(prevProps, prevState) {
    if (this.state.currentPage !== prevState.currentPage) {
      this.fetchTodos();
    }
  }

  fetchTodos = () => {
    const { getTodosList, page } = this.props;
    const offset = (this.state.currentPage - 1) * 10;
    getTodosList(ACTIONS.TODOS.GET_TODOS, {
      limit: this.state.perPage,
      offset,
      page,
    });
  };

  render() {
    const { page, inProgress, Data } = this.props;
    const { pageTitle, headerActions, redirect } = pageConfig[page];
    const Theader = (
      <tr>
        <th>PDF</th>
        <th>Assets Count</th>
        <th>Status</th>
        <th>Link</th>
      </tr>
    );
    let customKey = 0;
    // const Tbody =
    //   Data &&
    //   Data.pdf_storages &&
    //   Data.pdf_storages.results &&
    //   Data.pdf_storages.results.length > 0 &&
    //   Data.pdf_storages.results.map(each => {
    //     const {
    //       assets_count: AssetsCount,
    //       pdf_id: PdfId,
    //       state: Status
    //     } = each;
    //     customKey += 1;
    //     return (
    //       <tr key={customKey}>
    //         <td id="pdfId">{PdfId.slice(0, 7)}</td>
    //         <td id="assetsCount">{AssetsCount}</td>
    //         <td id="status">{Status}</td>
    //         <td id="pdfLink">-</td>
    //       </tr>
    //     );
    //   });

    const totalPages =
      Data && Data.pdf_storages
        ? Math.ceil(Data.pdf_storages.count / this.state.perPage)
        : 0;
    const paginationData = {
      start: this.state.start,
      end: totalPages > 5 ? this.state.end : totalPages,
      currentPage: this.state.currentPage,
      onPagination: this.onPagination,
      onPageChange: this.onPageChange,
      totalPages: totalPages < 1 ? 1 : totalPages
    };
    // const isAssetDetailsFound = Tbody && Tbody.length > 0;
    // const table = (
    //   <Table header={Theader} body={Tbody} paginationData={paginationData} />
    // );
    const data = [
      {title:"Nowshad", description: "Okajhsd"},
      {title:"Nowshad", description: "Okajhsd"},
      {title:"Nowshad", description: "Okajhsd"},
      {title:"Nowshad", description: "Okajhsd"},
      {title:"Nowshad", description: "Okajhsd"},
      {title:"Nowshad", description: "Okajhsd"},
    ]
    let todoKey = 0;
    return (
      <div>
        <PageLoader inProgress={inProgress} />
        <PageHeader
          {...this.props}
          title={pageTitle}
          actions={headerActions}
          redirect={redirect}
        />

        <div className="row todo-list">
        {data && data.map(each=>{
          todoKey++;
          return <CustomList data={each} key={todoKey}/>
        })}
        </div>
      </div>
    );
  }
}

Todos.propTypes = {
  getTodosList: PropTypes.func.isRequired,
  page: PropTypes.string.isRequired,
  match: PropTypes.object.isRequired,
  inProgress: PropTypes.bool.isRequired,
  Data: PropTypes.object,
  history: PropTypes.object.isRequired
};

Todos.defaultProps = {
  Data: {}
};
const mapStateToProps = state => ({
  Data: state.todosReducerStore.data,
  inProgress: state.todosReducerStore.inProgress
});

const mapDispatchToProps = dispatch => {
  return {
    getTodosList: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    resetState: type => {
      dispatch(getActionsFormate(type));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(Todos)
);
