import React from 'react';
import PropTypes from 'prop-types';
import './UserInfo.scss';
import DataCards from '../DataCards/DataCards';

const UserInfo = props => {
  const { userData } = props;
  console.log("user data:", userData);

  const email = userData && userData.email ? userData.email : '';
  const departments = userData && userData.departments.map(depart=>{
    const Id = depart.id; 
    const title = depart.name ? depart.name:"Department";
    const body = (<div className="card-data-body user-email">Email: {userData.email}</div>);
    return {Id, title, body};
  })
  return (
    <div className="user-info">
      <div className="user-email"> {email}</div>
      <DataCards {...this.props} data={departments} actions={bodyHandler} />
    </div>
  );
};

UserInfo.propTypes = {
  userData: PropTypes.oneOfType([PropTypes.array, PropTypes.object]).isRequired
};

export default UserInfo;
