import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { reverse } from 'named-urls';
import pageConfig from '../../utils/pageConfig';
import PageLoader from '../../components/PageLoader/PageLoader';
import PageHeader from '../../components/PageHeader/PageHeader';
import Table from '../../components/Table/Table';
import { getActionsFormate, getFormatedDate } from '../../utils/common';
import {
  onPagination,
  onPageChange,
  getBodyComponenet
} from '../../utils/utils';
import { FOOTPRINTS_ROUTES } from '../../config';
import { ASSET_STATUS, DATA_NOT_FOUND, ROLES } from '../../utils/constants';
import { checkUserValidity } from '../../utils/authService';

class Assets extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      perPage: 10,
      currentPage: 1,
      start: 1,
      end: 5
    };
    this.onPagination = onPagination.bind(this);
    this.onPageChange = onPageChange.bind(this);
  }

  componentDidMount() {
    this.fetchAssets();
  }

  componentDidUpdate(prevProps, prevState) {
    if (this.state.currentPage !== prevState.currentPage) {
      this.fetchAssets();
    }
  }

  fetchAssets = () => {
    const { getAssetsList, page, match } = this.props;
    const { actions } = pageConfig[page];
    const type = actions && actions.fetchList;
    const offset = (this.state.currentPage - 1) * 10;
    const { params } = match;
    let location = false;
    if (checkUserValidity(false, ROLES.ASSET_MANAGER)) {
      location = params.orgUnitId;
    }
    getAssetsList(type, {
      limit: this.state.perPage,
      offset,
      page,
      location
    });
  };

  showAssetDetails = assetId => {
    const { history, match } = this.props;
    const { orgUnitId } = match.params;
    let url = reverse(FOOTPRINTS_ROUTES.ASSET.DETAIL.SHOW, { assetId });
    if (checkUserValidity(false, ROLES.ASSET_MANAGER) && orgUnitId) {
      url = reverse(FOOTPRINTS_ROUTES.ORG_UNITS.DETAIL.ASSET.DETAIL.SHOW, {
        orgUnitId,
        assetId
      });
    }
    history.push(url);
    return true;
  };

  render() {
    const { page, inProgress, Data } = this.props;
    const { pageTitle } = pageConfig[page];
    const Theader = (
      <tr>
        <th>Asset</th>
        <th>Name</th>
        <th>Type</th>
        <th>Submitted Date</th>
        <th>Due Date</th>
        <th>Status</th>
      </tr>
    );
    let customKey = 0;
    const Tbody =
      Data &&
      Data.results &&
      Data.results.length > 0 &&
      Data.results.map(each => {
        const {
          asset_type: AssetType,
          due_date: DueDate,
          qr_id: QrId,
          state: Status,
          submitted_date: SubmittedDate,
          name: AssetName
        } = each;
        customKey += 1;
        return (
          <tr
            key={customKey}
            onClick={eve => {
              this.showAssetDetails(QrId);
            }}
          >
            <td id="qrId">{QrId.slice(0, 7)}</td>
            <td id="assetName">{AssetName || '-'}</td>
            <td id="assetType">{AssetType.name}</td>
            <td id="submittedDate">
              {getFormatedDate(SubmittedDate).formatedDate}
            </td>
            <td id="dueDate">{getFormatedDate(DueDate).formatedDate}</td>
            <td id="status">{ASSET_STATUS[Status]}</td>
          </tr>
        );
      });

    const totalPages = Data ? Math.ceil(Data.count / this.state.perPage) : 0;
    const paginationData = {
      start: this.state.start,
      end: totalPages > 5 ? this.state.end : totalPages,
      currentPage: this.state.currentPage,
      onPagination: this.onPagination,
      onPageChange: this.onPageChange,
      totalPages: totalPages < 1 ? 1 : totalPages
    };
    const isAssetDetailsFound = Tbody && Tbody.length > 0;
    const table = (
      <Table header={Theader} body={Tbody} paginationData={paginationData} />
    );
    return (
      <div>
        <PageLoader inProgress={inProgress} />
        <PageHeader {...this.props} title={pageTitle} />
        {getBodyComponenet(table, isAssetDetailsFound, DATA_NOT_FOUND[page])}
      </div>
    );
  }
}

Assets.propTypes = {
  getAssetsList: PropTypes.func.isRequired,
  page: PropTypes.string.isRequired,
  match: PropTypes.object.isRequired,
  inProgress: PropTypes.bool.isRequired,
  Data: PropTypes.object,
  history: PropTypes.object.isRequired
};

Assets.defaultProps = {
  Data: {}
};
const mapStateToProps = state => ({
  Data: state.assetsReducerStore.data,
  inProgress: state.assetsReducerStore.inProgress
});

const mapDispatchToProps = dispatch => {
  return {
    getAssetsList: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    resetState: type => {
      dispatch(getActionsFormate(type));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(Assets)
);
