import React from 'react';
import Tooltip from '@atlaskit/tooltip';
import Select, { components } from 'react-select';

const Input = props => {
  if (props.isHidden) {
    return <components.Input {...props} />;
  }
  return (
    <div>
      <Tooltip content="Custom Input">
        <components.Input {...props} />
      </Tooltip>
    </div>
  );
};

const CustomSelect = props => {
  return (
    <Select
      closeMenuOnSelect={false}
      components={{ Input }}
      onChange={props.onChange}
      options={props.departmentsFieldValue}
      isMulti
      placeholder="Departments"
    />
  );
}
AddEditUserForm.propTypes = {
  handleSubmit: PropTypes.func.isRequired,
  formErrors: PropTypes.object.isRequired,
  emailFieldId: PropTypes.string.isRequired,
  emailFieldValue: PropTypes.string.isRequired,
  handleChange: PropTypes.func.isRequired,
  firstNameFieldId: PropTypes.string.isRequired,
  firstNameFieldValue: PropTypes.string.isRequired,
  lastNameFieldId: PropTypes.string.isRequired,
  lastNameFieldValue: PropTypes.string.isRequired,
  handleDepartments: PropTypes.func.isRequired,
  departmentsFieldValue: PropTypes.string.isRequired,
  departmentsFieldId: PropTypes.string.isRequired,
  disabled: PropTypes.bool.isRequired
};

AddEditUserForm.defaultProps = {};

export default CustomSelect;
