import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Button } from 'react-bootstrap';
import { withRouter } from 'react-router-dom';

import ACTIONS from '../../apiConfig/actions.constants';
import AsOnboardingCard from '../../hocs/AsOnbaordingCard';
import { PASSWORDS_NO_MATCH, INTERNAL_SERVER } from '../../utils/errorMessages';
import PasswordResetForm from '../../components/PasswordResetForm';
import {
  checkPasswordStrength,
  getErrorResMsg
} from '../../utils/formValidations';
import {
  PASSWORD_RESET_HEADER,
  CALL_STATUS,
  PASSWORD_RESET_TOKEN_HEADER,
  PAGE
} from '../../utils/constants';
import { redirectToLogin } from '../../utils/authService';
import CustomButton from '../../components/CustomButton/CustomButton';

class PasswordReset extends React.Component {
  PASSWORD_FIELD_ONE_ID = 'password-one';

  PASSWORD_FIELD_TWO_ID = 'password-two';

  constructor(props) {
    super(props);
    this.state = {
      submitDisabled: true,
      [this.PASSWORD_FIELD_TWO_ID]: '',
      [this.PASSWORD_FIELD_ONE_ID]: '',
      formErrors: {},
      errorCode: {}
    };
  }

  componentDidMount() {
    this.props.dispatch({
      type: ACTIONS.PASSWORD_RESET.VERIFY_TOKEN,
      headers: {
        [PASSWORD_RESET_TOKEN_HEADER]: this.getPasswordResetTokenFromUrl()
      }
    });
  }

  static getDerivedStateFromProps(nextProps, state) {
    const { errorCode, callStatus } = nextProps.passwordReset;
    if (
      callStatus !== CALL_STATUS.TOKEN_ERROR &&
      errorCode &&
      errorCode !== state.errorCode
    ) {
      const errMsg = getErrorResMsg(errorCode, PAGE.RESET_PASSWORD);
      return { formErrors: errMsg, errorCode };
    }
    return {};
  }

  getPasswordResetTokenFromUrl = () => {
    const { params } = this.props.match;
    return params && params.token;
  };

  enableSubmit = () => {
    const passwordOne = this.state[this.PASSWORD_FIELD_ONE_ID];
    const passwordTwo = this.state[this.PASSWORD_FIELD_TWO_ID];
    const disabled = !(passwordOne.length > 0 && passwordTwo.length > 0);
    this.setState({ submitDisabled: disabled });
  };

  handleChange = event => {
    this.setState(
      { [event.target.id]: event.target.value, formErrors: {} },
      () => this.enableSubmit()
    );
  };

  validateForm = () => {
    const [passwordOneValidity, passwordOneErrors] = checkPasswordStrength(
      this.state[this.PASSWORD_FIELD_ONE_ID],
      'Password'
    );

    const formErrors = {};
    const isEqual =
      this.state[this.PASSWORD_FIELD_ONE_ID] ===
      this.state[this.PASSWORD_FIELD_TWO_ID];
    const formValidity = passwordOneValidity && isEqual;
    if (!isEqual) {
      formErrors[this.PASSWORD_FIELD_TWO_ID] = [PASSWORDS_NO_MATCH];
    }

    this.setState({ formErrors: { ...passwordOneErrors, ...formErrors } });

    return formValidity;
  };

  handleSubmit = event => {
    event.preventDefault();
    this.props.dispatch({
      type: ACTIONS.PASSWORD_RESET.RESET_STATE,
      payload: { callStatus: this.props.passwordReset.callStatus }
    });
    if (this.validateForm()) {
      const data = {
        new_password: this.state[this.PASSWORD_FIELD_ONE_ID],
        confirm_password: this.state[this.PASSWORD_FIELD_TWO_ID]
      };
      this.submit(data);
    }
  };

  submit = data => {
    this.setState({ errorCode: '' });
    this.props.dispatch({
      type: ACTIONS.PASSWORD_RESET.PASSWORD_RESET,
      data,
      headers: {
        [PASSWORD_RESET_TOKEN_HEADER]: this.getPasswordResetTokenFromUrl()
      }
    });
  };

 getButton = id => {
  const buttonText = 'Go to Login';
   return (
      <CustomButton
        disabled={this.props.passwordReset.inProgress}
        isLoading={this.props.passwordReset.inProgress}
        submitFunction={redirectToLogin}
        id={id}
        className="btn onboarding-btn"
        type="button"
        text={buttonText}
    />
   )
 }
  render() {
    const { passwordReset } = this.props;
    const { callStatus, errorMsg } = passwordReset;
    const errorMessage =
      Object.values(errorMsg).length > 0 ? Object.values(errorMsg)[0] : '';
    const passwordResetSuccess = (
      <div className="card-text text-left">
        New password has been set.
        {this.getButton("passwordResetSuccesRedir")}
      </div>
    );
    let component = null;
    if (callStatus === CALL_STATUS.TOKEN_ERROR && errorMessage) {
      component = (
        <div className="card-text text-left">
          {errorMessage}
          {!errorMessage.includes(INTERNAL_SERVER) && (
           this.getButton("passwordResetError")
            // <Button
            //   block
            //   type="button"
            //   className="btn onboarding-btn"
            //   onClick={() => redirectToLogin()}
            //   disabled={this.props.passwordReset.inProgress}
            // >
            //   {buttonText}
            // </Button>


          )}
        </div>
      );
    } else if (this.props.passwordReset.callStatus === CALL_STATUS.RESPONDED) {
      component = passwordResetSuccess;
    } else if (
      callStatus === CALL_STATUS.TOKEN_RESPONDED ||
      callStatus === CALL_STATUS.ERROR
    ) {
      component = (
        <PasswordResetForm
          disabled={this.state.submitDisabled}
          handleSubmit={this.handleSubmit}
          formErrors={this.state.formErrors}
          passwordFieldOneId={this.PASSWORD_FIELD_ONE_ID}
          passwordFieldOneValue={this.state[this.PASSWORD_FIELD_ONE_ID]}
          passwordFieldOneName={this.PASSWORD_FIELD_ONE_ID}
          passwordFieldTwoId={this.PASSWORD_FIELD_TWO_ID}
          passwordFieldTwoValue={this.state[this.PASSWORD_FIELD_TWO_ID]}
          passwordFieldTwoName={this.PASSWORD_FIELD_TWO_ID}
          handleChange={this.handleChange}
          inProgress={this.props.passwordReset.inProgress}
        />
      );
    }

    return (
      <div className="password-reset-container">
        <h4 className="card-title fp-card-title mb-5">
          {PASSWORD_RESET_HEADER}
        </h4>
        {component}
      </div>
    );
  }
}

PasswordReset.propTypes = {
  match: PropTypes.object.isRequired,
  dispatch: PropTypes.func.isRequired,
  passwordReset: PropTypes.object.isRequired
};

const mapStateToProps = state => ({
  passwordReset: state.passwordResetStore
});

export default withRouter(
  connect(mapStateToProps)(AsOnboardingCard(PasswordReset))
);
