import React from 'react';
import PropTypes from 'prop-types';
import { faTrashAlt } from '@fortawesome/free-regular-svg-icons';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import './Todo.scss';

class Todo extends React.Component {
    constructor(props){
        super(props);
        this.state={
            showDescription: false
        }
    }

    handleDescription = ()=>{
        this.setState({showDescription:!this.state.showDescription})
    }

    render(){
        const {data} = this.props;
        return (
        <div className="card border todo">
             <div className="col col-7 m-2 p-2" onClick={eve=>this.handleDescription()}>
                <div className="p-1 todo-title">{data.title}</div>
                {this.state.showDescription && <div className="p-1 todo-description">{data.description}</div>}  
            </div>
            <div className="col col-2"></div>
            <FontAwesomeIcon className="col col-2"icon={faTrashAlt} />
        </div>
       )
    }
}

Todo.propTypes = {
    data: PropTypes.object,
    paginationData: PropTypes.object.isRequired
};

Todo.defaultProps = {
    data: {}
};
  export default Todo;
