import React from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import AddEditUserForm from '../../components/AddEditUserForm/AddEditUserForm';
import { checkEmail } from '../../utils/formValidations';
import PageHeader from '../../components/PageHeader/PageHeader';
import pageConfig from '../../utils/pageConfig';
import { getActionsFormate } from '../../utils/common';
import { STORE, ROLES } from '../../utils/constants';

class AddEditUser extends React.Component {
  FIRST_NAME_FIELD_ID = 'aeu-fname';

  LAST_NAME_FIELD_ID = 'aeu-lname';

  EMAIL_FIELD_ID = 'aeu-email';

  DEPARTMENTS_FIELD_ID = 'select-departments';

  USER_ROLES_ID = 'user-roles';

  constructor(props) {
    super(props);
    this.state = {
      formErrors: {},
      formValidity: false,
      [this.FIRST_NAME_FIELD_ID]: '',
      [this.LAST_NAME_FIELD_ID]: '',
      [this.EMAIL_FIELD_ID]: '',
      [this.DEPARTMENTS_FIELD_ID]: [],
      [this.USER_ROLES_ID]:[],
      errorCode: '',
      disabled: true
    };
  }

  componentDidMount() {
    const { getDepartments, page, getUserData, match } = this.props;
    const dtype = pageConfig[page].actions.getDepartments;
    getDepartments(dtype);
    const utype = pageConfig[page].actions.fetchUser;
    if (utype) {
      const { params } = match;
      getUserData(utype, { ...params });
    }
  }

  validateForm = () => {
    const [emailValidity, emailErrors] = checkEmail(
      this.state[this.EMAIL_FIELD_ID],
      'Email'
    );
    const formValidity = emailValidity;
    const formErrors = { ...emailErrors };

    this.setState({ formValidity, formErrors });

    return formValidity;
  };

  enableSubmit = (email, firstName, lastName, departments, userRoles) => {
    const disabled = !(
      email.length > 0 &&
      firstName.length > 0 &&
      lastName.length > 0 &&
      departments.length > 0 &&
      userRoles.length > 0
    );
    this.setState({ disabled });
  };

  handleChange = event => {
    this.setState(
      {
        [event.target.id]: event.target.value,
        formErrors: {}
      },
      () =>
        this.enableSubmit(
          this.state[this.EMAIL_FIELD_ID],
          this.state[this.FIRST_NAME_FIELD_ID],
          this.state[this.LAST_NAME_FIELD_ID],
          this.state[this.DEPARTMENTS_FIELD_ID],
          this.state[this.USER_ROLES_ID]
        )
    );
  };

  handleDepartments = event => {
    this.setState(
      {
        [this.DEPARTMENTS_FIELD_ID]: event || [],
        formErrors: {}
      },
      () =>
        this.enableSubmit(
          this.state[this.EMAIL_FIELD_ID],
          this.state[this.FIRST_NAME_FIELD_ID],
          this.state[this.LAST_NAME_FIELD_ID],
          this.state[this.DEPARTMENTS_FIELD_ID],
          this.state[this.USER_ROLES_ID]
        )
    );
  };

  getDepartmentIds = departments => {
    const departIds = departments.map(depart => {
      return depart.value;
    });
    return departIds;
  };

  getUserRoleIds = userRoles => {
    const userRolesIds = userRoles.map(role=>{
      return ROLES.indexOf(role)
    })
    return userRolesIds;
  }
  handleSubmit = event => {
    event.preventDefault();
    // this.props.dispatch({ type: ACTIONS.AUTH.RESET_STATE });
    if (this.validateForm()) {
      const data = {
        email: this.state[this.EMAIL_FIELD_ID],
        first_name: this.state[this.FIRST_NAME_FIELD_ID],
        last_name: this.state[this.LAST_NAME_FIELD_ID],
        department: this.getDepartmentIds(this.state[this.DEPARTMENTS_FIELD_ID]),
        user_roles: this.getUserRoleIds(this.state[this.USER_ROLES_ID])
      };
      const type = pageConfig[this.props.page].actions.postData;
      this.props.submit(type, { data, props: { ...this.props } });
    }
  };

  // getDepartment=()=>{

  //   const {userDepartments} = this.props;

  //   const localDepart = this.state[this.DEPARTMENTS_FIELD_ID];
  //   let customDepart = [];
  //   if(localDepart.length === 0){
  //     customDepart = userDepartments;
  //   }else if(localDepart.length === userDepartments.length){
  //     customDepart = localDepart;
  //   }else{
  //      customDepart = localDepart;
  //     for(let udepart of userDepartments){
  //       let checkExistDepart = false;
  //       for(let ldepart of localDepart){
  //         if(ldepart.value === udepart.value){
  //           checkExistDepart = true;
  //         }
  //       }
  //       if(!checkExistDepart){
  //         customDepart.push(udepart);
  //       }
  //     }
  //   }
  //   return customDepart;
  // }

  // getStateValues = () => {
  //   const { userData } = this.props;

  //   if (userData.length > 0 && this.state.disabled) {
  //     const firstName = userData.first_name;
  //     const lastName = userData.last_name;
  //     const { email } = userData;
  //     const departments = userData.department.map(depart => {
  //       return { value: depart.id, label: depart.name };
  //     });
  //     return { firstName, lastName, email, departments };
  //   }
  //   return {
  //     firstName: this.state[this.FIRST_NAME_FIELD_ID],
  //     lastName: this.state[this.LAST_NAME_FIELD_ID]
  //   };
  // };

  render() {
    const { page } = this.props;
    const { pageTitle } = pageConfig[page];
    // const stateValues = this.getStateValues();
    return (
      <div className="add-edit-user">
        <PageHeader title={pageTitle} />
        <AddEditUserForm
          handleSubmit={this.handleSubmit}
          handleChange={this.handleChange}
          handleDepartments={this.handleDepartments}
          firstNameFieldId={this.FIRST_NAME_FIELD_ID}
          lastNameFieldId={this.LAST_NAME_FIELD_ID}
          emailFieldId={this.EMAIL_FIELD_ID}
          firstNameFieldValue={this.state[this.FIRST_NAME_FIELD_ID]}
          lastNameFieldValue={this.state[this.LAST_NAME_FIELD_ID]}
          emailFieldValue={this.state[this.EMAIL_FIELD_ID]}
          departmentsFieldValue={this.props.userDepartments}
          departmentsFieldId={this.DEPARTMENTS_FIELD_ID}
          userRoles = {this.USER_ROLES_ID}
          formErrors={this.formErrors}
          disabled={this.state.disabled}
        />
      </div>
    );
  }
}

AddEditUser.propTypes = {
  page: PropTypes.string.isRequired,
  userDepartments: PropTypes.array.isRequired,
  // userData: PropTypes.object.isRequired,
  match: PropTypes.object.isRequired,
  getDepartments: PropTypes.func.isRequired,
  getUserData: PropTypes.func.isRequired,
  submit: PropTypes.func.isRequired
};

AddEditUser.defaultProps = {};

const mapStateToProps = state => ({
  userDepartments: state.addEditUserReducerStore[STORE.GET_USER_DEPARTMENTS],
  userData: state.DetailsReducerStore.data
});

const mapDispatchToProps = dispatch => {
  return {
    getDepartments: (dtype, data) => {
      dispatch(getActionsFormate(dtype, data));
    },
    submit: (type, data) => {
      dispatch(getActionsFormate(type, data));
    },
    getUserData: (type, data) => {
      dispatch(getActionsFormate(type, data));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(AddEditUser)
);
