import React from 'react';
import PropTypes from 'prop-types';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import ERROR_CODES from '../../utils/errorCodes';
import ACTIONS from '../../apiConfig/actions.constants';
import { ALERT_MESSAAGES } from '../../utils/constants';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import { getActionsFormate } from '../../utils/common';
notify = (props) =>{
    const { AlertMessageStore, resetState } = props;
    const { status, errorCode, page } = AlertMessageStore;
    if(status === ACTIONS.ALERT_MESSAGE.ADD_SUCCESS){
        toast.success(ALERT_MESSAAGES[ACTIONS.ALERT_MESSAGE.ADD_SUCCESS], {
            position: toast.POSITION.TOP_RIGHT
        });
    }
    if(status === ACTIONS.ALERT_MESSAGE.UPDATE_SUCCESS){
        toast.success(ALERT_MESSAAGES[ACTIONS.ALERT_MESSAGE.UPDATE_SUCCESS], {
            position: toast.POSITION.TOP_RIGHT  
        });
    }
    if(status === ACTIONS.ALERT_MESSAGE.FAIL && page){
        toast.error(ERROR_CODES[page][errorCode], {
            position: toast.POSITION.TOP_RIGHT,
        });
    }
    const type = ACTIONS.ALERT_MESSAGE.RESET_STATE;
    resetState(type);
};

const AlertMessage =(props)=>  {

    return (
        <div>
            {this.notify(props)}
            <ToastContainer />
        </div>
    )
}


AlertMessage.propTypes = {
    AlertMessageStore:PropTypes.func.isRequired,
    resetState: PropTypes.func.isRequired
};

const mapStateToProps = state => ({
    AlertMessageStore: state.AlertMessageReducerStore
});

const mapDispatchToProps = dispatch => {
    return {
      resetState: type => {
        dispatch(getActionsFormate(type));
      }
    };
  };
export default withRouter(
    connect(
      mapStateToProps,
      mapDispatchToProps
    )(AlertMessage)
  );
