import React from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import PropTypes from 'prop-types';
import C3Chart from 'react-c3js';
import 'c3/c3.css';
import { getActionsFormate } from '../../utils/common';
import pageConfig from '../../utils/pageConfig';
import { STORE } from '../../utils/constants';
import DataNotFound from '../../components/DataNotFound/DataNotFound';
import './Analytics.scss';

class Analytics extends React.Component {
  componentDidMount() {
    const { makeCall, page, match } = this.props;
    const { actions } = pageConfig[page];
    const { fetchAssetsCount, fetchGenericAssetsCount } = actions;
    const { params } = match;
    if (fetchAssetsCount) {
      makeCall(fetchAssetsCount, { ...params, page });
    }
    if (fetchGenericAssetsCount) {
      makeCall(fetchGenericAssetsCount, { ...params, page });
    }
  }

  componentDidUpdate(prevProps) {
    if (prevProps.match.url !== this.props.match.url) {
      const { makeCall, page, match } = this.props;
      const { actions } = pageConfig[page];
      const { fetchAssetsCount, fetchGenericAssetsCount } = actions;
      const { params } = match;
      if (fetchAssetsCount) {
        makeCall(fetchAssetsCount, { ...params, page });
      }
      if (fetchGenericAssetsCount) {
        makeCall(fetchGenericAssetsCount, { ...params, page });
      }
    }
  }

  render() {
    const { AssetTypeCount, GenericAssetsCount } = this.props;
    const {
      count: AssignedAssetsCount,
      all_assets_count: AllAssetsCount
    } = GenericAssetsCount;
    let AssetTypeAnalytics = '';
    if (AssetTypeCount && Object.keys(AssetTypeCount).length > 0) {
      const { formatedData, size, isAllEmpty, legend } = AssetTypeCount;
      AssetTypeAnalytics = (
        <div className="row">
          <div className="col col-2" />
          <div className="card col col-8">
            <span className="assetsCountOnAssetType pt-2 pb-2">
              Assets of Asset Types
            </span>
            {!isAllEmpty ? (
              <C3Chart size={size} data={formatedData} legend={legend} />
            ) : (
              <DataNotFound name="Assets Count" />
            )}
          </div>
          <div className="col col-2" />
        </div>
      );
    }
    return (
      <div>
        <div className="row">
          <div className="col col-2" />
          <div className="col col-8 genericAssetsCount">
            <div className="row mb-3 mt-3">
              <div className="col col-5 border card p-4 ">
                Total Assets: 
                <div className="">{AllAssetsCount || 0}</div>
              </div>
              <div className="col col-5 border ml-auto card p-4">
                Assigned Assets: {AssignedAssetsCount || 0}{' '}
              </div>
            </div>
          </div>
          <div className="col col-2" />
        </div>
        {AssetTypeAnalytics}
      </div>
    );
  }
}

Analytics.propTypes = {
  AssetTypeCount: PropTypes.object.isRequired,
  makeCall: PropTypes.func.isRequired,
  match: PropTypes.object.isRequired,
  page: PropTypes.string.isRequired,
  GenericAssetsCount: PropTypes.object.isRequired
};

Analytics.defaultProps = {};
const mapStateToProps = (state, props) => {
  const { page } = props;
  const formateFunction = pageConfig[page].formateFunc;
  return {
    AssetTypeCount: formateFunction.assetTypeCount(
      state.analyticsReducerStore[STORE.GET_ASSETS_COUNT_WRT_ASSET_TYPE]
    ),
    GenericAssetsCount:
      state.analyticsReducerStore[STORE.GET_GENERIC_ASSETS_COUNT],
    inProgress: state.listReducerStore.inProgress
  };
};

const mapDispatchToProps = dispatch => {
  return {
    makeCall: (type, data) => {
      dispatch(getActionsFormate(type, data));
    }
  };
};

export default withRouter(
  connect(
    mapStateToProps,
    mapDispatchToProps
  )(Analytics)
);
