import { include } from 'named-urls';
import { faObjectGroup, faBuilding } from '@fortawesome/free-regular-svg-icons';
import {
  faUsers,
  faShapes,
  faChartLine
} from '@fortawesome/free-solid-svg-icons';

import DataCards from '../components/DataCards/DataCards';
import ACTIONS from './apiConfig/actions.constants';

const userSessionCookie = 'upayogakarta';
const rememberUserCookie = 'yaadrakhna';

const FOOTPRINTS_ROUTES = {
  LOGIN: '/login/',
  ANALYTICS: '/analytics/',
  DASHBOARD: '/dashboard/',

  ORG_UNITS: include('/org-units/', {
    LIST: '',
    ADD: 'add/',
    DETAIL: include(':orgUnitId/', {
      SHOW: '',
      MODIFY: 'modify/'
    })
  }),

  USERS: include('/users/', {
    LIST: '',
    ADD: 'add/',
    DETAIL: include(':userId/', {
      SHOW: '',
      MODIFY: 'modify/'
    })
  }),

  ASSET_TYPES: include('/asset-types/', {
    LIST: '',
    ADD: 'add/',
    DETAIL: include(':assetTypeId/', {
      SHOW: '',
      MODIFY: 'modify/'
    })
  }),

  ASSET_ATTRIBUTES: include('/asset-attrs/', {
    LIST: '',
    ADD: 'add/',
    DETAIL: include(':assetAttrId/', {
      SHOW: '',
      MODIFY: 'modify/'
    })
  }),

  FORGOT_PASSWORD: '/forgot-password',
  PASSWORD_RESET: '/password-reset/:token'
};

const SIDENAV_ITEMS = [
  {
    position: 1,
    displayName: 'Analytics',
    route: FOOTPRINTS_ROUTES.ANALYTICS,
    icon: faChartLine
  },
  {
    position: 2,
    displayName: 'Org Units',
    route: FOOTPRINTS_ROUTES.ORG_UNITS.LIST,
    icon: faBuilding
  },
  {
    position: 3,
    displayName: 'Users',
    route: FOOTPRINTS_ROUTES.USERS.LIST,
    icon: faUsers
  },
  {
    position: 4,
    displayName: 'Asset Types',
    route: FOOTPRINTS_ROUTES.ASSET_TYPES.LIST,
    icon: faObjectGroup
  },
  {
    position: 5,
    displayName: 'Asset Attributes',
    route: FOOTPRINTS_ROUTES.ASSET_ATTRIBUTES.LIST,
    icon: faShapes
  }
];

const COMPONENTS = {
  DATA_CARD:{
    component:DataCards,
    job: {
      PAGE_DATA:ACTIONS.USERS.GET_USER_LIST
    },
    actions: {
      onComponent:showUserDetails,
      edit:editDetails,
      delete: deleteDetails
    }
  },
  USER:{
    component:DataCards,
    job: {
      PAGE_DATA:ACTIONS.USERS.GET_USER_LIST
    },
    actions: {
    }
  }
}

export {
  userSessionCookie,
  rememberUserCookie,
  FOOTPRINTS_ROUTES,
  SIDENAV_ITEMS
};
