import React from 'react';
import './OrgUnitDetails.scss';
import PropTypes from 'prop-types';
import DataCards from '../DataCards/DataCards';

const OrgUnitDetails = props => {
  const { Data, actions, cardFormate } = props;
  if(Data && Object.values(Data).length>0){
    const { parent, child } = Data;
    const cardData = cardFormate(child);
    const hideCard = cardData.length > 0;
    const parentElement = parent ? parent.name : '-';
    return (
      <div className="org-unit-details">
        {parent && (
          <div className="row org-unit-parent mb-2">
            <span className=" col col-3 org-unit-details-key">parent</span>
            <span className="">:</span>
            <span className="col org-unit-details-value">{parentElement}</span>
          </div>
        )}
        {hideCard && (
          <div>
            <div className="org-unit-children-title pb-3 pt-3">Org units</div>
            <DataCards {...props} data={cardData} actions={actions} />
          </div>
        )}
      </div>
    );
  };
}

OrgUnitDetails.propTypes = {
  Data: PropTypes.object.isRequired,
  actions: PropTypes.object.isRequired,
  cardFormate: PropTypes.func.isRequired
};

OrgUnitDetails.defaultProps = {
  Data: {}
};

export default OrgUnitDetails;
