import React, { createRef } from 'react';
import { InputGroup, FormControl } from 'react-bootstrap';
import PropTypes from 'prop-types';
import { faTrashAlt, faPlus, faMinus } from '@fortawesome/free-solid-svg-icons';
import './AddEditAttributesForm.scss';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';

const AddEditAttributesForm = props => {
  return (
    <div>
      {props.attributes.map((attribute, i) => {
        const ref = createRef();
        return (
          <div key={i} ref={ref} className="row ">
            <div className="card col col-11 col-lg-11 col-md-11 border mb-3">
              <div className="row pt-2">
                <div className="col-5">Name</div>
                <div className="col-6">
                  Values
                  {attribute.errors.values ? (
                    <p className="error-text">{attribute.errors.values}</p>
                  ) : null}
                </div>
                <div className="col-1 text-center">
                  <button
                    onClick={() => props.addValueText(attribute.id, ref)}
                    className="delete-attribute"
                  >
                    <FontAwesomeIcon className="" icon={faPlus} />
                  </button>
                </div>
              </div>
              {attribute.errors.attributes ? (
                <p className="error-text">{attribute.errors.attributes}</p>
              ) : null}
              {attribute.values.map((value, j) => {
                const valueref = createRef();
                return (
                  <div ref={valueref} key={j} className="row">
                    {j === 0 ? (
                      <InputGroup className="col-5" size="sm">
                        <FormControl
                          type="text"
                          placeholder="Attribute Name"
                          name={attribute.id}
                          value={attribute.name}
                          onChange={
                            event =>
                              props.handleAttributeChange(event, attribute.id)
                            // eslint-disable-next-line
                          }
                        />
                      </InputGroup>
                    ) : (
                      <div className="col-5" />
                    )}
                    <div className="col col-6">
                      <InputGroup size="sm">
                        <FormControl
                          type="text"
                          placeholder="Value"
                          value={value.name}
                          onChange={
                            event =>
                              props.handleValueChange(
                                event,
                                attribute.id,
                                value.id
                              )
                            // eslint-disable-next-line
                          }
                        />
                      </InputGroup>
                    </div>
                    <div className="col-1 text-center">
                      <button
                        className="delete-attribute"
                        disabled={
                          attribute.values.length === 1 ||
                          (props.edit && j < attribute.initial)
                        }
                        onClick={
                          () =>
                            props.deleteValueText(
                              attribute.id,
                              value.id,
                              valueref
                            )
                          // eslint-disable-next-line
                        }
                      >
                        <FontAwesomeIcon
                          className="delete-button"
                          size="xs"
                          icon={faMinus}
                        />
                      </button>
                    </div>
                  </div>
                );
              })}
            </div>
            <div className="col col-1">
              <button
                disabled={props.attributes.length === 1}
                onClick={() => props.deleteAttributeCard(ref, attribute.id)}
                className="delete-attribute"
              >
                <FontAwesomeIcon className="delete-button" icon={faTrashAlt} />
              </button>
            </div>
          </div>
        );
      })}
      <div className="row" ref={props.mainref}>
        <button
          className="float-right col col-1 offset-lg-10 add-more-button"
          onClick={() => props.addAttributeCard(props.mainref)}
          disabled={props.edit}
        >
          Add More...
        </button>
      </div>
      <div className="row">
        <button
          className="float-right col col-2 offset-lg-9 mt-5 btn btn-sm btn-primary"
          onClick={props.handleSubmit}
        >
          Submit
        </button>
      </div>
    </div>
  );
};

AddEditAttributesForm.propTypes = {
  attributes: PropTypes.array.isRequired,
  handleAttributeChange: PropTypes.func.isRequired,
  addValueText: PropTypes.func.isRequired,
  handleValueChange: PropTypes.func.isRequired,
  deleteValueText: PropTypes.func.isRequired,
  deleteAttributeCard: PropTypes.func.isRequired,
  mainref: PropTypes.any.isRequired,
  addAttributeCard: PropTypes.func.isRequired,
  handleSubmit: PropTypes.func.isRequired,
  edit: PropTypes.bool.isRequired
};

AddEditAttributesForm.defaultProps = {};

export default AddEditAttributesForm;
